"""Supabase client initialization and configuration."""

from functools import lru_cache
from typing import TYPE_CHECKING, Any

if TYPE_CHECKING:
    from supabase import Client
else:
    try:
        from supabase import create_client, Client
        SUPABASE_AVAILABLE = True
    except ImportError:
        SUPABASE_AVAILABLE = False
        Client = Any  # type: ignore
        create_client = None  # type: ignore

from app.core.config import get_settings


class SupabaseError(Exception):
    """Raised when Supabase operations fail."""


@lru_cache
def get_supabase_client() -> Client | None:
    """Return a configured Supabase client or None if unavailable."""

    if not SUPABASE_AVAILABLE or create_client is None:
        return None

    settings = get_settings()

    if not settings.supabase_url or not settings.supabase_service_role_key:
        return None

    return create_client(
        settings.supabase_url,
        settings.supabase_service_role_key
    )


def require_supabase() -> Client:
    """Return Supabase client or raise if not configured."""

    client = get_supabase_client()
    if client is None:
        raise SupabaseError(
            "Supabase is not configured. Please set SUPABASE_URL and "
            "SUPABASE_SERVICE_ROLE_KEY environment variables."
        )
    return client

src/app/services/auth.py.content:
"""Simple authentication service for the MVP stage."""

from __future__ import annotations

import hashlib
import re
import secrets
import logging
from dataclasses import dataclass
from datetime import UTC, datetime, timedelta
from enum import Enum
from typing import Dict, Optional, Any

try:
    from supabase import Client
except ImportError:
    Client = None  # type: ignore

try:
    import bcrypt
    BCRYPT_AVAILABLE = True
except ImportError:
    BCRYPT_AVAILABLE = False

from app.services.email import send_verification_email

logger = logging.getLogger(__name__)


class AuthError(Exception):
    """Base class for authentication related errors."""

    status_code: int = 400

    def __init__(self, message: str) -> None:
        super().__init__(message)
        self.message = message


class UserAlreadyExists(AuthError):
    """Raised when attempting to register a duplicate email."""

    status_code = 409


class InvalidCredentials(AuthError):
    """Raised when login credentials fail validation."""

    status_code = 401


class PasswordPolicyError(AuthError):
    """Raised when the supplied password does not meet requirements."""

    status_code = 422


class AccountStatus(str, Enum):
    """Account states stored in Supabase ``auth_accounts`` table."""

    PENDING_VERIFICATION = "pending_verification"
    ACTIVE = "active"
    DISABLED = "disabled"


@dataclass
class AuthUser:
    """Represents an authenticated account."""

    id: str
    email: str
    full_name: str
    password_hash: str
    status: AccountStatus
    created_at: datetime
    updated_at: datetime
    last_login_at: Optional[datetime] = None


@dataclass
class AuthRegistration:
    """Represents an onboarding registration awaiting verification."""

    id: str
    email: str
    full_name: str
    password_hash: str
    verification_token: Optional[str]
    verification_sent_at: Optional[datetime]
    verification_expires_at: Optional[datetime]
    status: str
    created_at: datetime
    updated_at: datetime


@dataclass
class RegistrationResult:
    """Outcome of a user registration attempt."""

    account: AuthUser
    registration: AuthRegistration

    # Convenience accessors -------------------------------------------------
    @property
    def id(self) -> str:
        """Proxy to the underlying account identifier."""

        return self.account.id

    @property
    def email(self) -> str:
        """Expose the registered email like an ``AuthUser`` instance."""

        return self.account.email

    @property
    def full_name(self) -> str:
        return self.account.full_name

    @property
    def status(self) -> AccountStatus:
        return self.account.status

    @property
    def password_hash(self) -> str:
        return self.account.password_hash


def _hash_password(raw: str) -> str:
    """Hash password using bcrypt if available, fallback to SHA-256.

    Note: SHA-256 is NOT recommended for production. This fallback exists
    only for development/testing environments without bcrypt installed.
    """
    if BCRYPT_AVAILABLE:
        # Use bcrypt with a cost factor of 12 (good balance of security and performance)
        salt = bcrypt.gensalt(rounds=12)
        hashed = bcrypt.hashpw(raw.encode('utf-8'), salt)
        return hashed.decode('utf-8')
    else:
        logger.warning("bcrypt not available, using SHA-256 fallback (NOT SECURE for production!)")
        return hashlib.sha256(raw.encode("utf-8")).hexdigest()


def _verify_password(raw: str, hashed: str) -> bool:
    """Verify password against hash using appropriate method.

    Detects whether the hash is bcrypt or SHA-256 and uses the correct verification.
    """
    if hashed.startswith('$2b$') or hashed.startswith('$2a$'):
        # This is a bcrypt hash
        if not BCRYPT_AVAILABLE:
            logger.error("Attempting to verify bcrypt hash but bcrypt not installed!")
            return False
        try:
            return bcrypt.checkpw(raw.encode('utf-8'), hashed.encode('utf-8'))
        except Exception as e:
            logger.error(f"bcrypt verification failed: {e}")
            return False
    else:
        # Assume SHA-256 hash (legacy/fallback)
        return secrets.compare_digest(hashed, hashlib.sha256(raw.encode("utf-8")).hexdigest())


class AccountNotFound(AuthError):
    """Raised when an account lookup fails."""

    status_code = 404


class VerificationError(AuthError):
    """Base error for verification flow."""


class VerificationTokenInvalid(VerificationError):
    """Raised when the supplied verification token is unknown."""

    status_code = 404


class VerificationTokenExpired(VerificationError):
    """Raised when the supplied verification token has expired."""

    status_code = 410


class SupabaseAuthRepository:
    """Real Supabase repository using auth_accounts and onboarding_registrations tables."""

    def __init__(self, db: Optional[Client] = None) -> None:
        self.db = db

    def _map_account(self, data: Dict[str, Any]) -> AuthUser:
        """Map Supabase row to AuthUser dataclass."""
        return AuthUser(
            id=data['id'],
            email=data['email'],
            full_name=data['full_name'],
            password_hash=data['password_hash'],
            status=AccountStatus(data['status']),
            created_at=datetime.fromisoformat(data['created_at']) if isinstance(data['created_at'], str) else data['created_at'],
            updated_at=datetime.fromisoformat(data['updated_at']) if isinstance(data['updated_at'], str) else data['updated_at'],
            last_login_at=datetime.fromisoformat(data['last_login_at']) if data.get('last_login_at') and isinstance(data['last_login_at'], str) else data.get('last_login_at'),
        )

    def _map_registration(self, data: Dict[str, Any]) -> AuthRegistration:
        """Map Supabase row to AuthRegistration dataclass."""
        return AuthRegistration(
            id=data['id'],
            email=data['email'],
            full_name=data['full_name'],
            password_hash=data['password_hash'],
            verification_token=data.get('verification_token'),
            verification_sent_at=datetime.fromisoformat(data['verification_sent_at']) if data.get('verification_sent_at') and isinstance(data['verification_sent_at'], str) else data.get('verification_sent_at'),
            verification_expires_at=datetime.fromisoformat(data['verification_expires_at']) if data.get('verification_expires_at') and isinstance(data['verification_expires_at'], str) else data.get('verification_expires_at'),
            status=data.get('status', 'registered'),
            created_at=datetime.fromisoformat(data['created_at']) if isinstance(data['created_at'], str) else data['created_at'],
            updated_at=datetime.fromisoformat(data['updated_at']) if isinstance(data['updated_at'], str) else data['updated_at'],
        )

    # ``auth_accounts`` helpers -------------------------------------------------
    def upsert_account(
        self,
        *,
        email: str,
        full_name: str,
        password_hash: str,
        status: AccountStatus,
    ) -> AuthUser:
        if not self.db:
            raise AuthError("Database not available")

        # Check if account exists
        existing = self.db.table('auth_accounts').select('*').eq('email', email).execute()

        if existing.data:
            # Update existing account
            account_data = {
                'full_name': full_name,
                'password_hash': password_hash,
                'status': status.value,
                'updated_at': datetime.now(UTC).isoformat()
            }
            result = self.db.table('auth_accounts').update(account_data).eq('email', email).execute()
            return self._map_account(result.data[0])
        else:
            # Create new account
            account_data = {
                'email': email,
                'full_name': full_name,
                'password_hash': password_hash,
                'status': status.value
            }
            result = self.db.table('auth_accounts').insert(account_data).execute()
            return self._map_account(result.data[0])

    def get_account_by_email(self, email: str) -> AuthUser:
        if not self.db:
            raise AuthError("Database not available")

        result = self.db.table('auth_accounts').select('*').eq('email', email).execute()

        if not result.data:
            raise AccountNotFound("Akun tidak ditemukan.")

        return self._map_account(result.data[0])

    def set_account_status(self, account_id: str, status: AccountStatus) -> AuthUser:
        if not self.db:
            raise AuthError("Database not available")

        update_data = {
            'status': status.value,
            'updated_at': datetime.now(UTC).isoformat()
        }
        result = self.db.table('auth_accounts').update(update_data).eq('id', account_id).execute()

        if not result.data:
            raise AccountNotFound("Akun tidak ditemukan.")

        return self._map_account(result.data[0])

    def record_login(self, account_id: str, timestamp: datetime) -> AuthUser:
        if not self.db:
            raise AuthError("Database not available")

        update_data = {
            'last_login_at': timestamp.isoformat(),
            'updated_at': timestamp.isoformat()
        }
        result = self.db.table('auth_accounts').update(update_data).eq('id', account_id).execute()

        if not result.data:
            raise AccountNotFound("Akun tidak ditemukan.")

        return self._map_account(result.data[0])

    # ``onboarding_registrations`` helpers -------------------------------------
    def upsert_registration(
        self,
        *,
        email: str,
        full_name: str,
        password_hash: str,
        token: str,
        expires_at: datetime,
    ) -> AuthRegistration:
        if not self.db:
            raise AuthError("Database not available")

        # Check if registration exists
        existing = self.db.table('onboarding_registrations').select('*').eq('email', email).execute()

        registration_data = {
            'email': email,
            'full_name': full_name,
            'password_hash': password_hash,
            'verification_token': token,
            'verification_sent_at': datetime.now(UTC).isoformat(),
            'verification_expires_at': expires_at.isoformat(),
            'status': 'registered'
        }

        if existing.data:
            # Update existing registration
            result = self.db.table('onboarding_registrations').update(registration_data).eq('email', email).execute()
            return self._map_registration(result.data[0])
        else:
            # Create new registration
            result = self.db.table('onboarding_registrations').insert(registration_data).execute()
            return self._map_registration(result.data[0])

    def get_registration_by_token(self, token: str) -> AuthRegistration:
        if not self.db:
            raise AuthError("Database not available")

        result = self.db.table('onboarding_registrations').select('*').eq('verification_token', token).execute()

        if not result.data:
            raise VerificationTokenInvalid("Token verifikasi tidak ditemukan.")

        return self._map_registration(result.data[0])

    def mark_registration_verified(self, registration_id: str) -> AuthRegistration:
        if not self.db:
            raise AuthError("Database not available")

        update_data = {
            'status': 'email_verified',
            'verification_token': None,
            'verification_expires_at': datetime.now(UTC).isoformat(),
            'updated_at': datetime.now(UTC).isoformat()
        }
        result = self.db.table('onboarding_registrations').update(update_data).eq('id', registration_id).execute()

        if not result.data:
            raise VerificationError("Registrasi tidak ditemukan.")

        return self._map_registration(result.data[0])


class InMemoryAuthRepository:
    """In-memory repository simulating Supabase ``auth`` tables.

    The tests exercise behaviour without a real Supabase instance, therefore the
    repository keeps state in dictionaries while mirroring the schema from the
    migration file.
    """

    def __init__(self) -> None:
        self._accounts_by_id: Dict[str, AuthUser] = {}
        self._accounts_by_email: Dict[str, str] = {}
        self._registrations_by_id: Dict[str, AuthRegistration] = {}
        self._registrations_by_token: Dict[str, str] = {}
        self._registrations_by_email: Dict[str, str] = {}

    # ``auth_accounts`` helpers -------------------------------------------------
    def upsert_account(
        self,
        *,
        email: str,
        full_name: str,
        password_hash: str,
        status: AccountStatus,
    ) -> AuthUser:
        now = datetime.now(UTC)
        existing_id = self._accounts_by_email.get(email)

        if existing_id:
            account = self._accounts_by_id[existing_id]
            account.full_name = full_name
            account.password_hash = password_hash
            account.status = status
            account.updated_at = now
        else:
            account_id = secrets.token_urlsafe(8)
            account = AuthUser(
                id=account_id,
                email=email,
                full_name=full_name,
                password_hash=password_hash,
                status=status,
                created_at=now,
                updated_at=now,
            )
            self._accounts_by_id[account_id] = account
            self._accounts_by_email[email] = account_id
        return account

    def get_account_by_email(self, email: str) -> AuthUser:
        try:
            account_id = self._accounts_by_email[email]
        except KeyError as exc:
            raise AccountNotFound("Akun tidak ditemukan.") from exc
        return self._accounts_by_id[account_id]

    def set_account_status(self, account_id: str, status: AccountStatus) -> AuthUser:
        account = self._accounts_by_id[account_id]
        account.status = status
        account.updated_at = datetime.now(UTC)
        return account

    def record_login(self, account_id: str, timestamp: datetime) -> AuthUser:
        account = self._accounts_by_id[account_id]
        account.last_login_at = timestamp
        account.updated_at = timestamp
        return account

    # ``onboarding_registrations`` helpers -------------------------------------
    def upsert_registration(
        self,
        *,
        email: str,
        full_name: str,
        password_hash: str,
        token: str,
        expires_at: datetime,
    ) -> AuthRegistration:
        now = datetime.now(UTC)
        existing_id = self._registrations_by_email.get(email)

        if existing_id:
            registration = self._registrations_by_id[existing_id]
            if registration.verification_token:
                self._registrations_by_token.pop(registration.verification_token, None)
            registration.full_name = full_name
            registration.password_hash = password_hash
            registration.verification_token = token
            registration.verification_expires_at = expires_at
            registration.verification_sent_at = now
            registration.updated_at = now
            registration.status = "registered"
            self._registrations_by_token[token] = existing_id
        else:
            registration_id = secrets.token_urlsafe(8)
            registration = AuthRegistration(
                id=registration_id,
                email=email,
                full_name=full_name,
                password_hash=password_hash,
                verification_token=token,
                verification_sent_at=now,
                verification_expires_at=expires_at,
                status="registered",
                created_at=now,
                updated_at=now,
            )
            self._registrations_by_id[registration_id] = registration
            self._registrations_by_email[email] = registration_id
            self._registrations_by_token[token] = registration_id
        return registration

    def get_registration_by_token(self, token: str) -> AuthRegistration:
        try:
            registration_id = self._registrations_by_token[token]
        except KeyError as exc:
            raise VerificationTokenInvalid("Token verifikasi tidak ditemukan.") from exc
        return self._registrations_by_id[registration_id]

    def mark_registration_verified(self, registration_id: str) -> AuthRegistration:
        registration = self._registrations_by_id[registration_id]
        registration.status = "email_verified"
        registration.updated_at = datetime.now(UTC)
        if registration.verification_token:
            self._registrations_by_token.pop(registration.verification_token, None)
        registration.verification_token = None
        registration.verification_expires_at = registration.updated_at
        return registration


class AuthService:
    """Authentication workflow backed by the Supabase repository."""

    def __init__(self, repository = None, db: Optional[Client] = None) -> None:
        if repository is not None:
            self._repository = repository
        elif db is not None:
            # Use Supabase if db client is provided
            self._repository = SupabaseAuthRepository(db)
        else:
            # Fall back to in-memory for tests
            self._repository = InMemoryAuthRepository()

    def register_user(self, *, email: str, full_name: str, password: str) -> RegistrationResult:
        normalized_email = email.strip().lower()

        logger.info(f"Registration attempt for email: {normalized_email}")

        # Validate inputs
        self._validate_email(normalized_email)
        self._validate_full_name(full_name)
        self._validate_password(password)

        password_hash = _hash_password(password)

        try:
            existing_account = self._repository.get_account_by_email(normalized_email)
        except AccountNotFound:
            existing_account = None

        if existing_account:
            logger.warning(f"Registration failed: email already exists {normalized_email}")
            raise UserAlreadyExists("Email sudah terdaftar. Silakan login.")

        verification_token = secrets.token_urlsafe(24)
        verification_expires = datetime.now(UTC) + timedelta(hours=24)

        try:
            account = self._repository.upsert_account(
                email=normalized_email,
                full_name=full_name.strip(),
                password_hash=password_hash,
                status=AccountStatus.PENDING_VERIFICATION,
            )

            registration = self._repository.upsert_registration(
                email=normalized_email,
                full_name=full_name.strip(),
                password_hash=password_hash,
                token=verification_token,
                expires_at=verification_expires,
            )

            send_verification_email(account.email, verification_token)

            logger.info(f"Registration successful for {normalized_email}, account_id: {account.id}")

            return RegistrationResult(account=account, registration=registration)
        except Exception as e:
            logger.error(f"Registration failed for {normalized_email}: {str(e)}")
            raise AuthError(f"Gagal mendaftarkan akun: {str(e)}")

    def verify_email(self, *, token: str) -> AuthUser:
        logger.info(f"Email verification attempt with token: {token[:10]}...")

        try:
            registration = self._repository.get_registration_by_token(token)
        except VerificationTokenInvalid as e:
            logger.warning(f"Email verification failed: invalid token {token[:10]}...")
            raise

        now = datetime.now(UTC)
        if registration.verification_expires_at and registration.verification_expires_at < now:
            logger.warning(f"Email verification failed: expired token for {registration.email}")
            raise VerificationTokenExpired(
                "Token verifikasi sudah kedaluwarsa. Silakan minta tautan verifikasi baru."
            )

        try:
            account = self._repository.get_account_by_email(registration.email)
            account = self._repository.set_account_status(account.id, AccountStatus.ACTIVE)
            self._repository.mark_registration_verified(registration.id)

            logger.info(f"Email verification successful for {registration.email}")
            return account
        except Exception as e:
            logger.error(f"Email verification failed for {registration.email}: {str(e)}")
            raise AuthError(f"Gagal memverifikasi email: {str(e)}")

    def verify_registration(self, *, token: str) -> AuthUser:
        return self.verify_email(token=token)

    def authenticate(self, *, email: str, password: str) -> AuthUser:
        normalized_email = email.strip().lower()

        logger.info(f"Authentication attempt for email: {normalized_email}")

        try:
            user = self._repository.get_account_by_email(normalized_email)
        except AccountNotFound as exc:
            logger.warning(f"Authentication failed: account not found for {normalized_email}")
            raise InvalidCredentials("Email atau password salah.") from exc

        if user.status is AccountStatus.DISABLED:
            logger.warning(f"Authentication failed: disabled account {normalized_email}")
            raise InvalidCredentials("Akun belum aktif. Selesaikan verifikasi email terlebih dahulu.")

        if not _verify_password(password, user.password_hash):
            logger.warning(f"Authentication failed: invalid password for {normalized_email}")
            raise InvalidCredentials("Email atau password salah.")

        now = datetime.now(UTC)
        self._repository.record_login(user.id, now)
        logger.info(f"Authentication successful for {normalized_email}")
        return user

    def _validate_email(self, email: str) -> None:
        pattern = r"^[^@\s]+@[^@\s]+\.[^@\s]+$"
        if not re.match(pattern, email):
            raise AuthError("Format email tidak valid.")

    def _validate_full_name(self, full_name: str) -> None:
        if len(full_name.strip()) < 3:
            raise AuthError("Nama lengkap minimal 3 karakter.")

    def _validate_password(self, password: str) -> None:
        if len(password) < 8:
            raise PasswordPolicyError("Password minimal 8 karakter.")
        if not re.search(r"[A-Za-z]", password) or not re.search(r"[0-9]", password):
            raise PasswordPolicyError("Password harus mengandung huruf dan angka.")


auth_service = AuthService()
"""Singleton instance to be shared across the application."""

src/app/services/products.py.content:
"""In-memory product catalog service with Sambatan toggles."""

from __future__ import annotations

import secrets
import re
import logging
from dataclasses import dataclass
from datetime import UTC, datetime
from typing import Dict, Iterable, Optional, List, Any

try:
    from supabase import Client
except ImportError:
    Client = None  # type: ignore

logger = logging.getLogger(__name__)


class ProductError(Exception):
    """Base error class for product operations."""

    status_code: int = 400

    def __init__(self, message: str) -> None:
        super().__init__(message)
        self.message = message


class ProductNotFound(ProductError):
    """Raised when attempting to access a missing product."""

    status_code = 404


def _ensure_utc(dt: datetime) -> datetime:
    if dt.tzinfo is None:
        return dt.replace(tzinfo=UTC)
    return dt.astimezone(UTC)


@dataclass
class Product:
    """Represents an item listed in the marketplace catalog."""

    id: str
    name: str
    base_price: int
    created_at: datetime
    updated_at: datetime
    is_sambatan_enabled: bool = False
    sambatan_total_slots: Optional[int] = None
    sambatan_deadline: Optional[datetime] = None

    def enable_sambatan(self, *, total_slots: int, deadline: datetime) -> None:
        if total_slots <= 0:
            raise ProductError("Total slot sambatan harus lebih dari 0.")
        deadline = _ensure_utc(deadline)
        now = datetime.now(UTC)
        if deadline <= now:
            raise ProductError("Deadline sambatan harus berada di masa depan.")

        self.is_sambatan_enabled = True
        self.sambatan_total_slots = total_slots
        self.sambatan_deadline = deadline
        self.updated_at = now

    def disable_sambatan(self) -> None:
        self.is_sambatan_enabled = False
        self.sambatan_total_slots = None
        self.sambatan_deadline = None
        self.updated_at = datetime.now(UTC)


class ProductService:
    """Product catalog service with Supabase persistence and fallback to in-memory."""

    def __init__(self, db: Optional[Client] = None) -> None:
        self.db = db
        self._products: Dict[str, Product] = {}  # Fallback for in-memory mode

    def _slugify(self, text: str) -> str:
        """Generate URL-friendly slug from text."""
        text = text.lower().strip()
        text = re.sub(r'[^\w\s-]', '', text)
        text = re.sub(r'[-\s]+', '-', text)
        return text

    def _map_product(self, data: Dict[str, Any]) -> Product:
        """Map Supabase row to Product dataclass."""
        return Product(
            id=data['id'],
            name=data['name'],
            base_price=int(data.get('price_low', 0)),
            created_at=datetime.fromisoformat(data['created_at']) if isinstance(data['created_at'], str) else data['created_at'],
            updated_at=datetime.fromisoformat(data['updated_at']) if isinstance(data['updated_at'], str) else data['updated_at'],
            is_sambatan_enabled=data.get('sambatan_enabled', False),
            sambatan_total_slots=data.get('sambatan_total_slots'),
            sambatan_deadline=datetime.fromisoformat(data['sambatan_deadline']) if data.get('sambatan_deadline') and isinstance(data['sambatan_deadline'], str) else data.get('sambatan_deadline'),
        )

    def create_product(
        self,
        *,
        name: str,
        base_price: int,
        brand_id: Optional[str] = None,
        description: Optional[str] = None,
    ) -> Product:
        if base_price <= 0:
            logger.warning(f"Product creation failed: invalid price {base_price}")
            raise ProductError("Harga dasar produk harus lebih dari 0 (minimal Rp 1).")

        logger.info(f"Creating product: {name} with price {base_price}")
        now = datetime.now(UTC)

        if self.db:
            # Use Supabase
            slug = self._slugify(name)
            product_data = {
                'name': name.strip(),
                'slug': slug,
                'description': description or '',
                'price_low': base_price,
                'status': 'draft',
                'is_active': False,
                'marketplace_enabled': False,
                'sambatan_enabled': False,
            }
            if brand_id:
                product_data['brand_id'] = brand_id

            result = self.db.table('products').insert(product_data).execute()
            return self._map_product(result.data[0])
        else:
            # Fallback to in-memory
            product_id = secrets.token_urlsafe(8)
            product = Product(
                id=product_id,
                name=name.strip(),
                base_price=base_price,
                created_at=now,
                updated_at=now,
            )
            self._products[product_id] = product
            return product

    def toggle_sambatan(
        self,
        *,
        product_id: str,
        enabled: bool,
        total_slots: Optional[int] = None,
        deadline: Optional[datetime] = None,
    ) -> Product:
        product = self.get_product(product_id)

        if enabled:
            if total_slots is None or deadline is None:
                raise ProductError("Total slot dan deadline wajib saat mengaktifkan Sambatan.")
            product.enable_sambatan(total_slots=total_slots, deadline=deadline)

            if self.db:
                # Update in Supabase
                update_data = {
                    'sambatan_enabled': True,
                    'updated_at': datetime.now(UTC).isoformat()
                }
                self.db.table('products').update(update_data).eq('id', product_id).execute()
        else:
            product.disable_sambatan()

            if self.db:
                # Update in Supabase
                update_data = {
                    'sambatan_enabled': False,
                    'updated_at': datetime.now(UTC).isoformat()
                }
                self.db.table('products').update(update_data).eq('id', product_id).execute()

        return product

    def get_product(self, product_id: str) -> Product:
        if self.db:
            # Use Supabase
            result = self.db.table('products').select('*').eq('id', product_id).execute()
            if not result.data:
                raise ProductNotFound("Produk tidak ditemukan.")
            return self._map_product(result.data[0])
        else:
            # Fallback to in-memory
            try:
                return self._products[product_id]
            except KeyError as exc:
                raise ProductNotFound("Produk tidak ditemukan.") from exc

    def list_products(self) -> Iterable[Product]:
        if self.db:
            # Use Supabase
            result = self.db.table('products').select('*').order('created_at', desc=True).execute()
            return [self._map_product(row) for row in result.data]
        else:
            # Fallback to in-memory
            return self._products.values()

    def search_products(
        self,
        query: Optional[str] = None,
        marketplace_only: bool = True,
        limit: int = 50
    ) -> List[Dict[str, Any]]:
        """Search products with filters."""
        if not self.db:
            # Fallback to listing all products
            return [
                {
                    'id': p.id,
                    'name': p.name,
                    'base_price': p.base_price,
                    'created_at': p.created_at.isoformat(),
                }
                for p in self._products.values()
            ]

        # Build Supabase query
        db_query = self.db.table('products').select('*')

        if marketplace_only:
            db_query = db_query.eq('marketplace_enabled', True)

        if query:
            # Simple text search
            db_query = db_query.or_(
                f"name.ilike.%{query}%,"
                f"description.ilike.%{query}%"
            )

        result = db_query.limit(limit).execute()
        return result.data

    def enable_marketplace(
        self,
        product_id: str,
        list_price: float,
        stock_on_hand: int
    ) -> None:
        """Enable a product for marketplace sales."""
        if not self.db:
            raise ProductError("Marketplace features require Supabase connection")

        # Update product
        self.db.table('products').update({
            'marketplace_enabled': True,
            'is_active': True,
            'status': 'active'
        }).eq('id', product_id).execute()

        # Create or update marketplace listing
        listing_data = {
            'product_id': product_id,
            'status': 'published',
            'list_price': list_price,
            'stock_on_hand': stock_on_hand,
            'stock_reserved': 0,
            'published_at': datetime.now(UTC).isoformat()
        }

        # Try to upsert (insert or update)
        existing = self.db.table('marketplace_listings').select('id').eq('product_id', product_id).execute()
        if existing.data:
            self.db.table('marketplace_listings').update(listing_data).eq('product_id', product_id).execute()
        else:
            self.db.table('marketplace_listings').insert(listing_data).execute()


product_service = ProductService()
"""Singleton product service instance shared across the app."""

src/app/services/sambatan.py.content:
"""Sambatan domain services with Supabase persistent storage."""

from __future__ import annotations

import secrets
from dataclasses import dataclass, field
from datetime import UTC, datetime
from enum import Enum
from typing import Dict, Iterable, List, Optional

try:
    from supabase import Client
    SUPABASE_AVAILABLE = True
except ImportError:
    Client = None  # type: ignore
    SUPABASE_AVAILABLE = False

from app.core.supabase import get_supabase_client, require_supabase


def _coerce_utc(value: Optional[datetime] = None) -> datetime:
    if value is None:
        return datetime.now(UTC)
    if value.tzinfo is None:
        return value.replace(tzinfo=UTC)
    return value.astimezone(UTC)


class SambatanError(Exception):
    """Base error class for Sambatan operations."""

    status_code: int = 400

    def __init__(self, message: str) -> None:
        super().__init__(message)
        self.message = message


class CampaignNotFound(SambatanError):
    status_code = 404


class CampaignClosed(SambatanError):
    status_code = 409


class InsufficientSlots(SambatanError):
    status_code = 409


class ParticipationNotFound(SambatanError):
    status_code = 404


class ParticipationStateInvalid(SambatanError):
    status_code = 409


class SambatanStatus(str, Enum):
    """State machine for Sambatan campaigns.

    Note: Database uses different enum values, we map between them.
    """

    INACTIVE = "inactive"
    ACTIVE = "active"
    FULL = "full"
    COMPLETED = "completed"
    FAILED = "failed"


class ParticipationStatus(str, Enum):
    """State transitions for individual participants."""

    RESERVED = "reserved"
    CONFIRMED = "confirmed"
    CANCELLED = "cancelled"
    REFUNDED = "refunded"


# Mapping between service enums and database enums
STATUS_TO_DB = {
    SambatanStatus.INACTIVE: "draft",
    SambatanStatus.ACTIVE: "active",
    SambatanStatus.FULL: "locked",
    SambatanStatus.COMPLETED: "fulfilled",
    SambatanStatus.FAILED: "expired",
}

DB_TO_STATUS = {v: k for k, v in STATUS_TO_DB.items()}

PARTICIPANT_STATUS_TO_DB = {
    ParticipationStatus.RESERVED: "pending_payment",
    ParticipationStatus.CONFIRMED: "confirmed",
    ParticipationStatus.CANCELLED: "cancelled",
    ParticipationStatus.REFUNDED: "refunded",
}

DB_TO_PARTICIPANT_STATUS = {v: k for k, v in PARTICIPANT_STATUS_TO_DB.items()}


@dataclass
class SambatanAuditLog:
    """Structured log entry for lifecycle transitions."""

    campaign_id: str
    event: str
    timestamp: datetime
    metadata: Dict[str, str] = field(default_factory=dict)


@dataclass
class SambatanParticipant:
    """Represents a participant in a Sambatan campaign."""

    id: str
    campaign_id: str
    user_id: str
    quantity: int
    status: ParticipationStatus
    joined_at: datetime
    updated_at: datetime
    shipping_address: str
    note: Optional[str] = None


@dataclass
class SambatanCampaign:
    """Aggregate root storing campaign status and counters."""

    id: str
    product_id: str
    title: str
    total_slots: int
    price_per_slot: int
    deadline: datetime
    status: SambatanStatus
    created_at: datetime
    updated_at: datetime
    slots_taken: int = 0
    payout_released: bool = False

    def slots_remaining(self) -> int:
        return max(self.total_slots - self.slots_taken, 0)

    def progress_percent(self) -> int:
        if self.total_slots == 0:
            return 0
        return min(int((self.slots_taken / self.total_slots) * 100), 100)


class SambatanService:
    """Coordinator for Sambatan campaign logic with persistent storage."""

    def __init__(
        self, 
        catalog_service: Optional["ProductService"] = None,
        db: Optional[Client] = None
    ) -> None:
        # Lazy import to avoid circular dependency
        if catalog_service is None:
            from app.services.products import product_service
            catalog_service = product_service
        self._product_service = catalog_service
        self._db = db

    def _get_db(self) -> Client:
        """Get database client, using provided or requiring supabase."""
        if self._db is not None:
            return self._db
        return require_supabase()

    # Campaign management -------------------------------------------------
    def create_campaign(
        self,
        *,
        product_id: str,
        title: str,
        total_slots: int,
        price_per_slot: int,
        deadline: datetime,
        now: Optional[datetime] = None,
    ) -> SambatanCampaign:
        now = _coerce_utc(now)
        deadline = _coerce_utc(deadline)

        product = self._product_service.get_product(product_id)
        if not product.is_sambatan_enabled:
            raise SambatanError("Produk belum diaktifkan untuk Sambatan.")

        if total_slots <= 0:
            raise SambatanError("Total slot harus lebih dari 0.")
        if price_per_slot <= 0:
            raise SambatanError("Harga per slot harus lebih dari 0.")
        if deadline <= now:
            raise SambatanError("Deadline kampanye harus di masa depan.")

        db = self._get_db()

        # Create campaign in database
        campaign_data = {
            'product_id': product_id,
            'title': title.strip(),
            'status': STATUS_TO_DB[SambatanStatus.ACTIVE],
            'total_slots': total_slots,
            'filled_slots': 0,
            'slot_price': float(price_per_slot),
            'deadline': deadline.isoformat(),
        }

        result = db.table('sambatan_campaigns').insert(campaign_data).execute()
        campaign_row = result.data[0]

        # Create audit log
        self._log(campaign_row['id'], "campaign_created", now, {"title": title.strip()})

        return self._map_campaign(campaign_row)

    def get_campaign(self, campaign_id: str) -> SambatanCampaign:
        db = self._get_db()
        result = db.table('sambatan_campaigns').select('*').eq('id', campaign_id).execute()

        if not result.data:
            raise CampaignNotFound("Kampanye sambatan tidak ditemukan.")

        return self._map_campaign(result.data[0])

    def list_campaigns(self) -> Iterable[SambatanCampaign]:
        db = self._get_db()
        result = db.table('sambatan_campaigns').select('*').order('created_at', desc=True).execute()
        return [self._map_campaign(row) for row in result.data]

    # Participation -------------------------------------------------------
    def join_campaign(
        self,
        *,
        campaign_id: str,
        user_id: str,
        quantity: int,
        shipping_address: str,
        note: str | None = None,
        now: Optional[datetime] = None,
    ) -> SambatanParticipant:
        now = _coerce_utc(now)
        db = self._get_db()

        # Get campaign and validate
        campaign = self.get_campaign(campaign_id)
        if campaign.status not in {SambatanStatus.ACTIVE}:
            raise CampaignClosed("Kampanye tidak menerima partisipan baru.")
        if now > campaign.deadline:
            raise CampaignClosed("Deadline kampanye telah berakhir.")
        if quantity <= 0:
            raise SambatanError("Minimal 1 slot per partisipasi.")
        if quantity > campaign.slots_remaining():
            raise InsufficientSlots("Slot sambatan tidak mencukupi.")

        # Atomically reserve slots using database function
        try:
            db.rpc('reserve_sambatan_slots', {
                'p_campaign_id': campaign_id,
                'p_slot_count': quantity
            }).execute()
        except Exception as e:
            if 'Insufficient slots' in str(e):
                raise InsufficientSlots("Slot sambatan tidak mencukupi.")
            elif 'Campaign is not active' in str(e):
                raise CampaignClosed("Kampanye tidak menerima partisipan baru.")
            raise SambatanError(f"Gagal mereservasi slot: {str(e)}")

        # Create participant
        participant_data = {
            'campaign_id': campaign_id,
            'profile_id': user_id,
            'slot_count': quantity,
            'contribution_amount': float(quantity * campaign.price_per_slot),
            'status': PARTICIPANT_STATUS_TO_DB[ParticipationStatus.RESERVED],
            'notes': note,
            'joined_at': now.isoformat(),
        }

        result = db.table('sambatan_participants').insert(participant_data).execute()
        participant_row = result.data[0]

        # Check if campaign is now full
        updated_campaign = self.get_campaign(campaign_id)
        if updated_campaign.status == SambatanStatus.FULL:
            self._log(campaign_id, "campaign_full", now, {"slots_taken": str(updated_campaign.slots_taken)})

        # Log participation
        self._log(
            campaign_id,
            "participant_joined",
            now,
            {"participant_id": participant_row['id'], "quantity": str(quantity)},
        )

        # Map participant with shipping address from notes
        participant_row['shipping_address'] = shipping_address
        return self._map_participant(participant_row)

    def cancel_participation(
        self,
        *,
        participation_id: str,
        reason: str | None = None,
        now: Optional[datetime] = None,
    ) -> SambatanParticipant:
        now = _coerce_utc(now)
        db = self._get_db()

        participant = self._get_participation(participation_id)
        if participant.status is not ParticipationStatus.RESERVED:
            raise ParticipationStateInvalid("Partisipasi tidak dapat dibatalkan pada status saat ini.")

        campaign = self.get_campaign(participant.campaign_id)

        # Update participant status
        db.table('sambatan_participants').update({
            'status': PARTICIPANT_STATUS_TO_DB[ParticipationStatus.CANCELLED],
            'cancelled_at': now.isoformat(),
        }).eq('id', participation_id).execute()

        # Atomically release slots using database function
        try:
            db.rpc('release_sambatan_slots', {
                'p_campaign_id': campaign.id,
                'p_slot_count': participant.quantity
            }).execute()
        except Exception as e:
            raise SambatanError(f"Gagal melepas slot: {str(e)}")

        # Log cancellation
        self._log(
            campaign.id,
            "participant_cancelled",
            now,
            {"participant_id": participant.id, "reason": reason or ""},
        )

        participant.status = ParticipationStatus.CANCELLED
        participant.updated_at = now
        return participant

    def confirm_participation(
        self,
        *,
        participation_id: str,
        now: Optional[datetime] = None,
    ) -> SambatanParticipant:
        now = _coerce_utc(now)
        db = self._get_db()

        participant = self._get_participation(participation_id)
        if participant.status is not ParticipationStatus.RESERVED:
            raise ParticipationStateInvalid("Konfirmasi hanya untuk partisipan aktif.")

        # Update participant status
        db.table('sambatan_participants').update({
            'status': PARTICIPANT_STATUS_TO_DB[ParticipationStatus.CONFIRMED],
            'confirmed_at': now.isoformat(),
        }).eq('id', participation_id).execute()

        self._log(
            participant.campaign_id,
            "participant_confirmed",
            now,
            {"participant_id": participant.id},
        )

        participant.status = ParticipationStatus.CONFIRMED
        participant.updated_at = now
        return participant

    def list_participants(self, campaign_id: str) -> List[SambatanParticipant]:
        self.get_campaign(campaign_id)  # Validate campaign exists
        db = self._get_db()

        result = db.table('sambatan_participants').select('*').eq('campaign_id', campaign_id).execute()
        return [self._map_participant(row) for row in result.data]

    # Lifecycle -----------------------------------------------------------
    def run_lifecycle(self, *, now: Optional[datetime] = None) -> List[SambatanAuditLog]:
        now = _coerce_utc(now)
        transitions: List[SambatanAuditLog] = []
        db = self._get_db()

        # Get all active campaigns
        result = db.table('sambatan_campaigns').select('*').in_(
            'status',
            [STATUS_TO_DB[SambatanStatus.ACTIVE], STATUS_TO_DB[SambatanStatus.FULL]]
        ).execute()

        for campaign_row in result.data:
            campaign = self._map_campaign(campaign_row)
            previous_status = campaign.status

            if campaign.status is SambatanStatus.FULL:
                self._complete_campaign(campaign, now)
                transitions.append(SambatanAuditLog(
                    campaign.id, 
                    "campaign_completed", 
                    now, 
                    {"slots_taken": str(campaign.slots_taken)}
                ))
            elif now > campaign.deadline:
                if campaign.slots_taken >= campaign.total_slots:
                    self._complete_campaign(campaign, now)
                    transitions.append(SambatanAuditLog(
                        campaign.id, 
                        "campaign_completed", 
                        now, 
                        {"slots_taken": str(campaign.slots_taken)}
                    ))
                else:
                    self._fail_campaign(campaign, now)
                    transitions.append(SambatanAuditLog(
                        campaign.id, 
                        "campaign_failed", 
                        now, 
                        {"slots_taken": str(campaign.slots_taken)}
                    ))

        return transitions

    def get_audit_logs(self, campaign_id: Optional[str] = None) -> List[SambatanAuditLog]:
        db = self._get_db()

        query = db.table('sambatan_audit_logs').select('*').order('created_at', desc=True)
        if campaign_id:
            query = query.eq('campaign_id', campaign_id)

        result = query.execute()
        return [self._map_audit_log(row) for row in result.data]

    # Dashboard -----------------------------------------------------------
    def get_dashboard_summary(self) -> Dict[str, int]:
        db = self._get_db()

        result = db.table('sambatan_campaigns').select('status, filled_slots').execute()

        campaigns = result.data
        total_campaigns = len(campaigns)
        active = len([c for c in campaigns if DB_TO_STATUS.get(c['status']) == SambatanStatus.ACTIVE])
        full = len([c for c in campaigns if DB_TO_STATUS.get(c['status']) == SambatanStatus.FULL])
        completed = len([c for c in campaigns if DB_TO_STATUS.get(c['status']) == SambatanStatus.COMPLETED])
        failed = len([c for c in campaigns if DB_TO_STATUS.get(c['status']) == SambatanStatus.FAILED])
        slots_taken = sum(c['filled_slots'] for c in campaigns)

        return {
            "total_campaigns": total_campaigns,
            "active_campaigns": active,
            "full_campaigns": full,
            "completed_campaigns": completed,
            "failed_campaigns": failed,
            "total_slots_taken": slots_taken,
        }

    # Internal helpers ----------------------------------------------------
    def _complete_campaign(self, campaign: SambatanCampaign, now: datetime) -> None:
        if campaign.status is SambatanStatus.COMPLETED:
            return

        db = self._get_db()

        # Use atomic database function to complete campaign
        try:
            db.rpc('complete_sambatan_campaign', {
                'p_campaign_id': campaign.id
            }).execute()
        except Exception as e:
            raise SambatanError(f"Gagal menyelesaikan kampanye: {str(e)}")

        self._log(campaign.id, "campaign_completed", now, {"slots_taken": str(campaign.slots_taken)})

    def _fail_campaign(self, campaign: SambatanCampaign, now: datetime) -> None:
        db = self._get_db()

        # Use atomic database function to fail campaign
        try:
            db.rpc('fail_sambatan_campaign', {
                'p_campaign_id': campaign.id
            }).execute()
        except Exception as e:
            raise SambatanError(f"Gagal membatalkan kampanye: {str(e)}")

        self._log(campaign.id, "campaign_failed", now, {"slots_taken": str(campaign.slots_taken)})

    def _get_participation(self, participation_id: str) -> SambatanParticipant:
        db = self._get_db()
        result = db.table('sambatan_participants').select('*').eq('id', participation_id).execute()

        if not result.data:
            raise ParticipationNotFound("Partisipan tidak ditemukan.")

        return self._map_participant(result.data[0])

    def _log(self, campaign_id: str, event: str, timestamp: datetime, metadata: Dict[str, str]) -> None:
        db = self._get_db()

        log_data = {
            'campaign_id': campaign_id,
            'event': event,
            'metadata': metadata,
            'created_at': timestamp.isoformat(),
        }

        db.table('sambatan_audit_logs').insert(log_data).execute()

    # Mapping helpers -----------------------------------------------------
    def _map_campaign(self, row: Dict) -> SambatanCampaign:
        """Map database row to SambatanCampaign dataclass."""
        return SambatanCampaign(
            id=row['id'],
            product_id=row['product_id'],
            title=row.get('title', ''),
            total_slots=row['total_slots'],
            price_per_slot=int(row['slot_price']),
            deadline=datetime.fromisoformat(row['deadline']) if row.get('deadline') else datetime.now(UTC),
            status=DB_TO_STATUS.get(row['status'], SambatanStatus.INACTIVE),
            created_at=datetime.fromisoformat(row['created_at']),
            updated_at=datetime.fromisoformat(row['updated_at']),
            slots_taken=row['filled_slots'],
            payout_released=False,
        )

    def _map_participant(self, row: Dict) -> SambatanParticipant:
        """Map database row to SambatanParticipant dataclass."""
        return SambatanParticipant(
            id=row['id'],
            campaign_id=row['campaign_id'],
            user_id=row.get('profile_id', ''),
            quantity=row['slot_count'],
            status=DB_TO_PARTICIPANT_STATUS.get(row['status'], ParticipationStatus.RESERVED),
            joined_at=datetime.fromisoformat(row['joined_at']),
            updated_at=datetime.fromisoformat(row.get('confirmed_at') or row.get('cancelled_at') or row['joined_at']),
            shipping_address=row.get('shipping_address', row.get('notes', '')),
            note=row.get('notes'),
        )

    def _map_audit_log(self, row: Dict) -> SambatanAuditLog:
        """Map database row to SambatanAuditLog dataclass."""
        return SambatanAuditLog(
            campaign_id=row['campaign_id'],
            event=row['event'],
            timestamp=datetime.fromisoformat(row['created_at']),
            metadata=row.get('metadata', {}),
        )


class SambatanLifecycleService:
    """Scheduler-friendly wrapper for lifecycle transitions."""

    def __init__(self, sambatan_service: SambatanService | None = None) -> None:
        self._sambatan_service = sambatan_service or SambatanService()
        self._last_run: Optional[datetime] = None

    def run(self, *, now: Optional[datetime] = None) -> List[SambatanAuditLog]:
        now = _coerce_utc(now)
        transitions = self._sambatan_service.run_lifecycle(now=now)
        self._last_run = now
        return transitions

    @property
    def last_run(self) -> Optional[datetime]:
        return self._last_run


# Singleton instances - will use Supabase when available
sambatan_service = SambatanService()
"""Singleton instance shared by routers and tests."""

sambatan_lifecycle_service = SambatanLifecycleService(sambatan_service)
"""Lifecycle helper to mimic background scheduler behaviour."""

src/app/api/routes/auth.py.content:
"""Authentication API endpoints."""

from __future__ import annotations

from datetime import datetime
from typing import TYPE_CHECKING, Any

from fastapi import APIRouter, Body, Depends, HTTPException, Request, Response, status
from pydantic import BaseModel, Field

if TYPE_CHECKING:
    from supabase import Client
else:
    try:
        from supabase import Client
    except ImportError:
        Client = Any  # type: ignore

from app.core.dependencies import get_db
from app.core.rate_limit import limiter, RATE_LIMITS
from app.services.auth import (
    AccountStatus,
    AuthService,
    AuthUser,
    AuthError,
    RegistrationResult,
    auth_service,
)

router = APIRouter(prefix="/api/auth", tags=["auth"])


class RegisterRequest(BaseModel):
    full_name: str = Field(..., min_length=3, max_length=120)
    email: str = Field(..., pattern=r"^[^@\s]+@[^@\s]+\.[^@\s]+$")
    password: str = Field(..., min_length=8, max_length=128)


class AuthPayload(BaseModel):
    user_id: str
    email: str
    full_name: str
    status: AccountStatus
    message: str


class RegisterResponse(BaseModel):
    registration_id: str
    account_id: str
    email: str
    full_name: str
    status: AccountStatus
    verification_expires_at: datetime
    message: str


class LoginRequest(BaseModel):
    email: str = Field(..., pattern=r"^[^@\s]+@[^@\s]+\.[^@\s]+$")
    password: str = Field(..., min_length=8, max_length=128)


class VerificationRequest(BaseModel):
    token: str = Field(..., min_length=8)


class VerificationResponse(BaseModel):
    user_id: str
    email: str
    full_name: str
    status: AccountStatus
    message: str


class SessionResponse(BaseModel):
    is_authenticated: bool
    user: dict | None = None


def get_auth_service(db: Client | None = Depends(get_db)) -> AuthService:
    if db:
        return AuthService(db=db)
    return auth_service


def _serialize_user(user: AuthUser) -> dict:
    return {
        "user_id": user.id,
        "email": user.email,
        "full_name": user.full_name,
        "status": user.status.value,
    }


def _handle_registration_result(result: RegistrationResult) -> RegisterResponse:
    registration = result.registration
    account = result.account
    expires_at = registration.verification_expires_at or account.updated_at
    return RegisterResponse(
        registration_id=registration.id,
        account_id=account.id,
        email=account.email,
        full_name=account.full_name,
        status=account.status,
        verification_expires_at=expires_at,
        message="Registrasi berhasil. Cek email Anda untuk tautan verifikasi.",
    )


@router.post("/register", response_model=RegisterResponse, status_code=status.HTTP_201_CREATED)
@limiter.limit(RATE_LIMITS["auth_register"])
def register_user(request: Request, payload: RegisterRequest, service: AuthService = Depends(get_auth_service)) -> RegisterResponse:
    try:
        result = service.register_user(
            email=payload.email,
            full_name=payload.full_name,
            password=payload.password,
        )
    except AuthError as exc:
        raise HTTPException(status_code=exc.status_code, detail=exc.message) from exc

    return _handle_registration_result(result)


@router.post("/verify", response_model=VerificationResponse)
@limiter.limit(RATE_LIMITS["auth_verify"])
def verify_user(
    request: Request, payload: VerificationRequest, service: AuthService = Depends(get_auth_service)
) -> VerificationResponse:
    try:
        user = service.verify_email(token=payload.token)
    except AuthError as exc:
        raise HTTPException(status_code=exc.status_code, detail=exc.message) from exc

    return VerificationResponse(
        user_id=user.id,
        email=user.email,
        full_name=user.full_name,
        status=user.status,
        message="Verifikasi berhasil",
    )


@router.post("/login", response_model=AuthPayload)
@limiter.limit(RATE_LIMITS["auth_login"])
async def login_user(
    request: Request,
    payload: LoginRequest | None = Body(None),
    service: AuthService = Depends(get_auth_service),
) -> AuthPayload:
    if payload is None:
        form = await request.form()
        form_dict = {key: value for key, value in form.items() if isinstance(value, str)}
        payload = LoginRequest(**form_dict)

    try:
        user = service.authenticate(email=payload.email, password=payload.password)
    except AuthError as exc:
        raise HTTPException(status_code=exc.status_code, detail=exc.message) from exc

    request.session["user"] = _serialize_user(user)

    return AuthPayload(
        user_id=user.id,
        email=user.email,
        full_name=user.full_name,
        status=user.status,
        message="Login berhasil",
    )


@router.post("/logout", status_code=status.HTTP_204_NO_CONTENT)
def logout_user(request: Request) -> Response:
    request.session.pop("user", None)
    return Response(status_code=status.HTTP_204_NO_CONTENT)


@router.get("/session", response_model=SessionResponse)
def read_session(request: Request) -> SessionResponse:
    user = request.session.get("user")
    return SessionResponse(is_authenticated=bool(user), user=user)

src/app/api/routes/onboarding.py.content:
"""API endpoints for the onboarding workflow."""

from __future__ import annotations

from datetime import datetime
from typing import Any, Dict, List, Type, TypeVar

from fastapi import APIRouter, Body, Depends, HTTPException, Request, status
from pydantic import BaseModel, Field, ValidationError

from app.services.onboarding import (
    OnboardingEvent,
    OnboardingService,
    OnboardingStatus,
    OnboardingError,
    onboarding_service,
)

router = APIRouter(prefix="/api/onboarding", tags=["onboarding"])

ModelT = TypeVar("ModelT", bound=BaseModel)


class RegistrationRequest(BaseModel):
    email: str = Field(..., pattern=r"^[^@\s]+@[^@\s]+\.[^@\s]+$", description="Email utama pengguna")
    full_name: str = Field(..., min_length=3, max_length=120)
    password: str = Field(..., min_length=8, max_length=128)
    marketing_opt_in: bool | None = Field(False, description="Apakah pengguna ingin menerima email komunitas")


class RegistrationResponse(BaseModel):
    onboarding_id: str
    status: OnboardingStatus
    verification_expires_at: datetime | None
    progress: Dict[str, Any]
    verification_token: str | None = Field(None, description="Token debug yang disediakan untuk lingkungan pengujian")


class VerificationRequest(BaseModel):
    onboarding_id: str
    token: str = Field(..., min_length=4, max_length=64)


class VerificationResponse(BaseModel):
    onboarding_id: str
    status: OnboardingStatus
    progress: Dict[str, Any]


class ProfileRequest(BaseModel):
    onboarding_id: str
    display_name: str = Field(..., min_length=2, max_length=80)
    business_goal: str = Field(..., min_length=3, max_length=160)
    experience_level: str = Field(..., min_length=3, max_length=120)


class ProfileResponse(BaseModel):
    onboarding_id: str
    status: OnboardingStatus
    profile: Dict[str, Any]
    progress: Dict[str, Any]


class EventLogResponse(BaseModel):
    onboarding_id: str
    events: List[Dict[str, Any]]


class ResendRequest(BaseModel):
    onboarding_id: str


def get_onboarding_service() -> OnboardingService:
    return onboarding_service


def _serialize_events(events: List[OnboardingEvent]) -> List[Dict[str, Any]]:
    return [
        {
            "event": event.event,
            "timestamp": event.timestamp.isoformat(),
            "metadata": event.metadata,
        }
        for event in events
    ]


def _serialize_progress(service: OnboardingService, onboarding_id: str) -> Dict[str, Any]:
    return service.get_progress(onboarding_id)


def _handle_error(exc: OnboardingError) -> None:
    raise HTTPException(status_code=exc.status_code, detail=exc.message)


async def _resolve_payload(
    model_cls: Type[ModelT],
    request: Request,
    payload: ModelT | None,
) -> ModelT:
    if payload is not None:
        return payload

    form = await request.form()
    data: Dict[str, Any] = {}
    for key, value in form.multi_items():
        data[key] = value

    try:
        return model_cls(**data)
    except ValidationError as exc:
        raise HTTPException(
            status_code=status.HTTP_422_UNPROCESSABLE_ENTITY,
            detail=exc.errors(),
        ) from exc


@router.post("/register", response_model=RegistrationResponse, status_code=status.HTTP_201_CREATED)
async def register_user(
    request: Request,
    payload: RegistrationRequest | None = Body(None),
    service: OnboardingService = Depends(get_onboarding_service),
) -> RegistrationResponse:
    payload = await _resolve_payload(RegistrationRequest, request, payload)
    try:
        user = service.register_user(
            email=payload.email,
            full_name=payload.full_name,
            password=payload.password,
            marketing_opt_in=bool(payload.marketing_opt_in),
        )
    except OnboardingError as exc:
        _handle_error(exc)

    progress = _serialize_progress(service, user.id)
    return RegistrationResponse(
        onboarding_id=user.id,
        status=user.status,
        verification_expires_at=progress["verification"]["expires_at"],
        progress=progress,
        verification_token=user.verification_token,
    )


@router.post("/verify", response_model=VerificationResponse)
async def verify_email(
    request: Request,
    payload: VerificationRequest | None = Body(None),
    service: OnboardingService = Depends(get_onboarding_service),
) -> VerificationResponse:
    payload = await _resolve_payload(VerificationRequest, request, payload)
    try:
        user = service.verify_email(
            onboarding_id=payload.onboarding_id,
            token=payload.token,
        )
    except OnboardingError as exc:
        _handle_error(exc)

    progress = _serialize_progress(service, user.id)
    return VerificationResponse(onboarding_id=user.id, status=user.status, progress=progress)


@router.post("/profile", response_model=ProfileResponse)
async def complete_profile(
    request: Request,
    payload: ProfileRequest | None = Body(None),
    service: OnboardingService = Depends(get_onboarding_service),
) -> ProfileResponse:
    payload = await _resolve_payload(ProfileRequest, request, payload)
    try:
        user = service.complete_profile(
            onboarding_id=payload.onboarding_id,
            display_name=payload.display_name,
            business_goal=payload.business_goal,
            experience_level=payload.experience_level,
        )
    except OnboardingError as exc:
        _handle_error(exc)

    progress = _serialize_progress(service, user.id)
    profile = progress["profile"] or {}
    return ProfileResponse(
        onboarding_id=user.id,
        status=user.status,
        profile=profile,
        progress=progress,
    )


@router.post("/resend", response_model=RegistrationResponse)
async def resend_token(
    request: Request,
    payload: ResendRequest | None = Body(None),
    service: OnboardingService = Depends(get_onboarding_service),
) -> RegistrationResponse:
    payload = await _resolve_payload(ResendRequest, request, payload)
    try:
        token = service.resend_verification_token(onboarding_id=payload.onboarding_id)
        user = service.get_user(payload.onboarding_id)
    except OnboardingError as exc:
        _handle_error(exc)

    progress = _serialize_progress(service, user.id)
    return RegistrationResponse(
        onboarding_id=user.id,
        status=user.status,
        verification_expires_at=progress["verification"]["expires_at"],
        progress=progress,
        verification_token=token,
    )


@router.get("/progress/{onboarding_id}", response_model=VerificationResponse)
def get_progress(
    onboarding_id: str,
    service: OnboardingService = Depends(get_onboarding_service),
) -> VerificationResponse:
    try:
        user = service.get_user(onboarding_id)
    except OnboardingError as exc:
        _handle_error(exc)

    progress = _serialize_progress(service, user.id)
    return VerificationResponse(onboarding_id=user.id, status=user.status, progress=progress)


@router.get("/events/{onboarding_id}", response_model=EventLogResponse)
def get_event_log(
    onboarding_id: str,
    service: OnboardingService = Depends(get_onboarding_service),
) -> EventLogResponse:
    try:
        service.get_user(onboarding_id)
    except OnboardingError as exc:
        _handle_error(exc)

    events = _serialize_events(service.get_events(onboarding_id))
    return EventLogResponse(onboarding_id=onboarding_id, events=events)

src/app/api/routes/profile.py.content:
"""Profile detail and follow interaction endpoints."""

from __future__ import annotations

from typing import Dict

from fastapi import APIRouter, Depends, Form, HTTPException, Query, Request, status
from fastapi.responses import HTMLResponse, RedirectResponse

from app.services.profile import (
    ProfileError,
    ProfileService,
    ProfileUpdate,
    ProfileView,
    profile_service,
)


router = APIRouter(prefix="/profile", tags=["profile"])


def get_profile_service() -> ProfileService:
    return profile_service


def _viewer_query_param(viewer_id: str | None) -> str:
    return f"viewer={viewer_id}" if viewer_id else ""


@router.get("/{username}", response_class=HTMLResponse, name="profile_detail")
async def profile_detail(
    username: str,
    request: Request,
    viewer: str | None = Query(default=None),
    service: ProfileService = Depends(get_profile_service),
) -> HTMLResponse:
    profile_view = await service.get_profile(username, viewer_id=viewer)
    templates = request.app.state.templates
    context = {
        "request": request,
        "profile_view": profile_view,
        "profile": profile_view.profile,
        "stats": profile_view.stats,
        "viewer_query": _viewer_query_param(profile_view.viewer.id),
    }
    return templates.TemplateResponse(request, "pages/profile/detail.html", context)


def _render_follow_button(
    request: Request,
    profile_view: ProfileView,
) -> HTMLResponse:
    templates = request.app.state.templates
    context = {
        "request": request,
        "profile_view": profile_view,
        "profile": profile_view.profile,
        "stats": profile_view.stats,
        "viewer_query": _viewer_query_param(profile_view.viewer.id),
    }
    return templates.TemplateResponse(request, "components/profile/follow_button.html", context)


@router.post("/{username}/follow", response_class=HTMLResponse, name="profile_follow")
async def follow_profile(
    username: str,
    request: Request,
    viewer: str | None = Query(default=None),
    service: ProfileService = Depends(get_profile_service),
) -> HTMLResponse:
    if not viewer:
        raise HTTPException(status_code=status.HTTP_401_UNAUTHORIZED, detail="Viewer wajib diisi")

    try:
        profile_view = await service.follow_profile(username, follower_id=viewer)
    except ProfileError as exc:
        raise HTTPException(status_code=exc.status_code, detail=exc.message) from exc

    return _render_follow_button(request, profile_view)


@router.api_route(
    "/{username}/follow",
    methods=["DELETE", "POST"],
    response_class=HTMLResponse,
    name="profile_unfollow",
)
async def unfollow_profile(
    username: str,
    request: Request,
    viewer: str | None = Query(default=None),
    service: ProfileService = Depends(get_profile_service),
) -> HTMLResponse:
    if not viewer:
        raise HTTPException(status_code=status.HTTP_401_UNAUTHORIZED, detail="Viewer wajib diisi")

    if request.method == "POST":
        form = await request.form()
        override = (form.get("_method") or form.get("intent") or "").strip().lower()
        if override not in {"delete", "unfollow"}:
            raise HTTPException(status_code=status.HTTP_405_METHOD_NOT_ALLOWED, detail="Metode tidak didukung")

    try:
        profile_view = await service.unfollow_profile(username, follower_id=viewer)
    except ProfileError as exc:
        raise HTTPException(status_code=exc.status_code, detail=exc.message) from exc

    return _render_follow_button(request, profile_view)


