  grid-template-columns: repeat(2, minmax(0, 1fr));
  gap: 2rem;
}

.policy-list {
  list-style: none;
  margin: 0;
  padding: 0;
  display: grid;
  gap: 1rem;
}

.policy-list li {
  padding: 1.3rem 1.5rem;
  display: grid;
  gap: 0.6rem;
}

.policy-tags {
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
}

.policy-tags span {
  padding: 0.35rem 0.8rem;
  border-radius: 999px;
  background: rgba(148, 163, 184, 0.15);
  border: 1px solid rgba(148, 163, 184, 0.25);
  font-size: 0.85rem;
}

.template-list,
.automation-list {
  list-style: none;
  margin: 0 0 1.25rem;
  padding: 0;
  display: grid;
  gap: 0.8rem;
}

.automation-list li {
  padding-left: 1.25rem;
  position: relative;
}

.automation-list li::before {
  content: "â€¢";
  position: absolute;
  left: 0;
  color: rgba(56, 189, 248, 0.75);
}

.mod-help {
  padding: 2.5rem;
  display: grid;
  gap: 2.25rem;
}

.help-grid {
  display: grid;
  grid-template-columns: repeat(2, minmax(0, 1fr));
  gap: 2rem;
}

.resource-list,
.contact-list {
  list-style: none;
  margin: 0;
  padding: 0;
  display: grid;
  gap: 1rem;
}

.resource-list li {
  padding: 1.2rem 1.4rem;
  display: grid;
  gap: 0.5rem;
}

.contact-list li {
  background: rgba(15, 23, 42, 0.55);
  border: 1px solid rgba(148, 163, 184, 0.18);
  border-radius: var(--radius-md);
  padding: 1rem 1.2rem;
}

.feedback-box {
  margin-top: 1.5rem;
  padding: 1.4rem 1.6rem;
  display: grid;
  gap: 0.75rem;
}

@media (max-width: 1024px) {
  .mod-hero,
  .overview-grid,
  .team-layout,
  .queue-grid,
  .curation-grid,
  .analytics-grid,
  .policy-grid,
  .help-grid {
    grid-template-columns: 1fr;
  }

  .mod-hero {
    padding: 2.25rem;
  }
}

@media (max-width: 768px) {
  .team-header,
  .team-row {
    grid-template-columns: repeat(2, minmax(0, 1fr));
  }

  .queue-table,
  .curation-table,
  .team-performance {
    display: block;
    overflow-x: auto;
    white-space: nowrap;
  }
}

src/app/web/static/css/nusantarum.css.content:
.nusantarum-page {
  display: grid;
  gap: 2.5rem;
  padding-block: 2rem 4rem;
  color: var(--text-primary);
}

.nusantarum-hero {
  display: grid;
  gap: 2rem;
  padding: clamp(2rem, 5vw, 3rem);
  border-radius: 28px;
  background: linear-gradient(135deg, rgba(15, 23, 42, 0.9), rgba(30, 41, 59, 0.65));
  border: 1px solid rgba(255, 255, 255, 0.18);
  backdrop-filter: blur(18px);
  box-shadow: 0 32px 80px rgba(15, 23, 42, 0.45);
}

.nusantarum-hero__copy {
  display: grid;
  gap: 1rem;
}

.nusantarum-hero__eyebrow {
  font-size: 0.85rem;
  letter-spacing: 0.28em;
  text-transform: uppercase;
  color: rgba(255, 255, 255, 0.65);
}

.nusantarum-hero h1 {
  font-family: 'Playfair Display', serif;
  font-size: clamp(2.5rem, 4vw, 3.5rem);
  font-weight: 700;
  letter-spacing: -0.02em;
}

.nusantarum-hero p {
  max-width: 60ch;
  font-size: 1rem;
  line-height: 1.7;
  color: rgba(255, 255, 255, 0.85);
}

.nusantarum-hero__stats {
  display: grid;
  gap: 1rem;
  grid-template-columns: repeat(auto-fit, minmax(160px, 1fr));
}

.nusantarum-hero__stats div {
  padding: 1.2rem 1.4rem;
  border-radius: 18px;
  background: rgba(255, 255, 255, 0.06);
  border: 1px solid rgba(255, 255, 255, 0.15);
}

.nusantarum-hero__stats dt {
  font-size: 0.85rem;
  text-transform: uppercase;
  letter-spacing: 0.14em;
  color: rgba(255, 255, 255, 0.65);
  margin-bottom: 0.35rem;
}

.nusantarum-hero__stats dd {
  font-size: clamp(1.8rem, 2vw + 1rem, 2.4rem);
  font-weight: 600;
  margin: 0;
}

.nusantarum-tabs {
  display: flex;
  flex-wrap: wrap;
  gap: 0.75rem;
}

.nusantarum-tab {
  display: inline-flex;
  align-items: center;
  gap: 0.65rem;
  padding: 0.6rem 1.3rem;
  border-radius: 999px;
  border: 1px solid transparent;
  background: rgba(255, 255, 255, 0.15);
  color: inherit;
  cursor: pointer;
  transition: transform 0.2s ease, box-shadow 0.2s ease, border 0.2s ease;
}

.nusantarum-tab[aria-selected='true'] {
  background: linear-gradient(135deg, rgba(251, 191, 36, 0.9), rgba(236, 72, 153, 0.85));
  border-color: rgba(255, 255, 255, 0.45);
  color: #0f172a;
  box-shadow: 0 12px 32px rgba(236, 72, 153, 0.35);
}

.nusantarum-tab:hover,
.nusantarum-tab:focus-visible {
  border-color: rgba(255, 255, 255, 0.5);
  transform: translateY(-2px);
}

.nusantarum-tab__count {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  min-width: 2rem;
  padding: 0.15rem 0.5rem;
  border-radius: 999px;
  background: rgba(15, 23, 42, 0.4);
  color: inherit;
  font-size: 0.85rem;
  font-weight: 600;
}

.nusantarum-controls {
  display: grid;
  gap: 1rem;
}

.nusantarum-search {
  display: flex;
  flex-wrap: wrap;
  gap: 1rem;
  align-items: center;
}

.nusantarum-search input[type='search'] {
  flex: 1;
  min-width: min(360px, 100%);
  padding: 0.85rem 1.2rem;
  border-radius: 18px;
  border: 1px solid rgba(255, 255, 255, 0.25);
  background: rgba(15, 23, 42, 0.55);
  color: inherit;
  font-size: 1rem;
  transition: border 0.2s ease, box-shadow 0.2s ease;
}

.nusantarum-search input[type='search']:focus {
  outline: none;
  border-color: rgba(251, 191, 36, 0.7);
  box-shadow: 0 0 0 2px rgba(251, 191, 36, 0.35);
}

.nusantarum-search input[type='search']::placeholder {
  color: rgba(255, 255, 255, 0.6);
}

.nusantarum-search-results {
  display: grid;
  gap: 1.2rem;
  padding: 1.3rem;
  border-radius: 20px;
  background: rgba(15, 23, 42, 0.65);
  border: 1px solid rgba(255, 255, 255, 0.18);
  box-shadow: inset 0 1px 0 rgba(255, 255, 255, 0.08);
}

.nusantarum-search-results__group {
  display: grid;
  gap: 0.6rem;
}

.nusantarum-search-results__group h3 {
  font-size: 0.9rem;
  text-transform: uppercase;
  letter-spacing: 0.18em;
  color: rgba(255, 255, 255, 0.6);
}

.nusantarum-search-results ul {
  list-style: none;
  margin: 0;
  padding: 0;
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
}

.nusantarum-search-results__pill {
  display: inline-flex;
  align-items: center;
  gap: 0.4rem;
  padding: 0.35rem 0.8rem;
  border-radius: 999px;
  background: rgba(255, 255, 255, 0.14);
  font-size: 0.85rem;
}

.nusantarum-search-results__message {
  font-size: 0.9rem;
  color: rgba(255, 255, 255, 0.7);
}

.nusantarum-search-results__placeholder {
  display: grid;
  gap: 0.75rem;
}

.nusantarum-body {
  display: grid;
  gap: 1.5rem;
}

.nusantarum-content {
  display: grid;
  gap: 1.5rem;
}

@media (min-width: 1024px) {
  .nusantarum-hero {
    grid-template-columns: minmax(0, 1fr) 320px;
    align-items: center;
  }

  .nusantarum-body {
    grid-template-columns: minmax(0, 320px) minmax(0, 1fr);
    align-items: start;
  }
}

.nusantarum-sidebar {
  position: relative;
}

.nusantarum-filter-panel {
  position: sticky;
  top: 1.5rem;
  display: grid;
  gap: 1.25rem;
  padding: 1.75rem;
  border-radius: 24px;
  background: rgba(15, 23, 42, 0.72);
  border: 1px solid rgba(255, 255, 255, 0.16);
}

.nusantarum-filter-panel h2 {
  font-size: 1.15rem;
  font-weight: 600;
  margin: 0;
}

.nusantarum-filter-panel h3 {
  font-size: 0.95rem;
  text-transform: uppercase;
  letter-spacing: 0.16em;
  color: rgba(255, 255, 255, 0.6);
  margin-bottom: 0.5rem;
}

.filter-actions {
  display: flex;
  justify-content: flex-end;
  margin-top: 0.5rem;
}

.filter-submit {
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.7rem 1.35rem;
  border-radius: 999px;
  border: 1px solid rgba(255, 255, 255, 0.28);
  background: linear-gradient(135deg, rgba(251, 191, 36, 0.88), rgba(236, 72, 153, 0.82));
  color: #0f172a;
  font-weight: 600;
  letter-spacing: 0.02em;
  box-shadow: 0 12px 32px rgba(236, 72, 153, 0.25);
  cursor: pointer;
  transition: transform 0.2s ease, box-shadow 0.2s ease, opacity 0.2s ease;
}

.filter-submit:hover,
.filter-submit:focus-visible {
  transform: translateY(-1px);
  box-shadow: 0 18px 40px rgba(236, 72, 153, 0.35);
  outline: none;
}

.filter-submit:active {
  transform: translateY(0);
  box-shadow: 0 8px 22px rgba(236, 72, 153, 0.28);
}

.text-muted {
  color: rgba(255, 255, 255, 0.7);
  font-size: 0.95rem;
  line-height: 1.6;
}

.filter-chip-group {
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
}

.filter-chip {
  position: relative;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  padding: 0.45rem 0.9rem;
  border-radius: 999px;
  border: 1px solid rgba(255, 255, 255, 0.25);
  background: transparent;
  color: inherit;
  cursor: pointer;
  overflow: hidden;
}

.filter-chip input[type='checkbox'] {
  position: absolute;
  inset: 0;
  width: 100%;
  height: 100%;
  opacity: 0;
  cursor: pointer;
}

.filter-chip span {
  pointer-events: none;
  transition: transform 0.2s ease;
}

.filter-chip input[type='checkbox']:checked + span {
  transform: translateY(-1px);
  font-weight: 600;
}

.toggle {
  display: inline-flex;
  gap: 0.75rem;
  align-items: center;
  font-size: 0.95rem;
}

.toggle input[type='checkbox'] {
  width: 1.15rem;
  height: 1.15rem;
  accent-color: #fbbf24;
}

.nusantarum-card-grid {
  display: grid;
  gap: 1.25rem;
  grid-template-columns: repeat(auto-fit, minmax(260px, 1fr));
}

.nusantarum-card {
  display: grid;
  gap: 1.2rem;
  padding: 1.6rem;
  border-radius: 22px;
  background: rgba(15, 23, 42, 0.68);
  border: 1px solid rgba(255, 255, 255, 0.16);
  transition: transform 0.2s ease, box-shadow 0.2s ease;
}

.nusantarum-card:hover,
.nusantarum-card:focus-within {
  transform: translateY(-4px);
  box-shadow: 0 18px 50px rgba(15, 23, 42, 0.5);
}

.nusantarum-card__header {
  display: flex;
  gap: 1rem;
  align-items: center;
}

.nusantarum-card__avatar {
  width: 3rem;
  height: 3rem;
  border-radius: 50%;
  background: linear-gradient(135deg, rgba(251, 191, 36, 0.9), rgba(236, 72, 153, 0.9));
  display: inline-flex;
  align-items: center;
  justify-content: center;
  font-weight: 600;
  color: #0f172a;
}

.nusantarum-card__title {
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
  font-size: 1.2rem;
  font-weight: 600;
}

.nusantarum-card__badge {
  display: inline-flex;
  align-items: center;
  gap: 0.3rem;
  padding: 0.25rem 0.6rem;
  border-radius: 999px;
  background: rgba(34, 197, 94, 0.16);
  color: rgba(134, 239, 172, 1);
  font-size: 0.75rem;
  text-transform: uppercase;
  letter-spacing: 0.12em;
}

.nusantarum-card__meta {
  margin: 0;
  font-size: 0.9rem;
  color: rgba(255, 255, 255, 0.7);
}

.nusantarum-card__description {
  margin: 0;
  font-size: 0.95rem;
  color: rgba(255, 255, 255, 0.78);
}

.nusantarum-card__stats {
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
  align-items: center;
}

.nusantarum-stat-pill {
  display: inline-flex;
  align-items: center;
  gap: 0.3rem;
  padding: 0.35rem 0.8rem;
  border-radius: 999px;
  background: rgba(255, 255, 255, 0.12);
  font-size: 0.85rem;
  color: rgba(255, 255, 255, 0.85);
}

.nusantarum-card__footer {
  display: flex;
  justify-content: space-between;
  align-items: center;
  gap: 1rem;
  flex-wrap: wrap;
}

.nusantarum-link {
  color: #fbbf24;
  text-decoration: none;
  font-weight: 600;
  transition: color 0.2s ease;
}

.nusantarum-link:hover,
.nusantarum-link:focus {
  color: #f59e0b;
  text-decoration: underline;
}

.nusantarum-table-wrapper {
  overflow-x: auto;
  border-radius: 20px;
  border: 1px solid rgba(255, 255, 255, 0.16);
  background: rgba(15, 23, 42, 0.72);
}

.nusantarum-table {
  width: 100%;
  border-collapse: collapse;
  min-width: 640px;
}

.nusantarum-table th,
.nusantarum-table td {
  padding: 1rem 1.25rem;
  border-bottom: 1px solid rgba(255, 255, 255, 0.08);
  text-align: left;
  vertical-align: top;
}

.nusantarum-table thead th {
  font-size: 0.85rem;
  text-transform: uppercase;
  letter-spacing: 0.18em;
  color: rgba(255, 255, 255, 0.65);
}

.nusantarum-table__sort {
  display: inline-flex;
  align-items: center;
  gap: 0.35rem;
  padding: 0;
  border: none;
  background: none;
  color: inherit;
  font: inherit;
  text-transform: inherit;
  letter-spacing: inherit;
  cursor: pointer;
}

.nusantarum-table__sort:hover,
.nusantarum-table__sort:focus-visible {
  color: rgba(255, 255, 255, 0.9);
  outline: none;
}

.nusantarum-table__sort-indicator {
  font-size: 0.85rem;
  opacity: 0.75;
}

.nusantarum-table tbody tr:last-child th,
.nusantarum-table tbody tr:last-child td {
  border-bottom: none;
}

.nusantarum-table__description {
  margin: 0.45rem 0 0;
  font-size: 0.9rem;
  color: rgba(255, 255, 255, 0.7);
}

.nusantarum-table__meta {
  font-size: 0.85rem;
  color: rgba(255, 255, 255, 0.6);
  margin: 0.25rem 0 0;
}

.nusantarum-aroma-group {
  display: flex;
  flex-wrap: wrap;
  gap: 0.4rem;
}

.nusantarum-badge {
  display: inline-flex;
  align-items: center;
  padding: 0.3rem 0.7rem;
  border-radius: 999px;
  background: rgba(251, 191, 36, 0.18);
  color: rgba(255, 255, 255, 0.85);
  font-size: 0.8rem;
  letter-spacing: 0.03em;
}

.nusantarum-empty {
  padding: 2.2rem;
  border-radius: 20px;
  background: rgba(15, 23, 42, 0.68);
  border: 1px solid rgba(255, 255, 255, 0.16);
  display: grid;
  gap: 0.75rem;
  justify-items: center;
  text-align: center;
}

.nusantarum-empty h3 {
  font-size: 1.3rem;
  margin: 0;
}

.nusantarum-empty__suggestions {
  list-style: disc;
  text-align: left;
  padding-left: 1.25rem;
  display: grid;
  gap: 0.35rem;
  font-size: 0.95rem;
  color: rgba(255, 255, 255, 0.75);
}

.nusantarum-pagination {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 1rem;
  flex-wrap: wrap;
}

.nusantarum-pagination__button {
  padding: 0.6rem 1.3rem;
  border-radius: 999px;
  border: 1px solid rgba(255, 255, 255, 0.25);
  background: rgba(15, 23, 42, 0.65);
  color: inherit;
  cursor: pointer;
  transition: transform 0.2s ease, box-shadow 0.2s ease, border 0.2s ease;
}

.nusantarum-pagination__button:hover,
.nusantarum-pagination__button:focus-visible {
  border-color: rgba(251, 191, 36, 0.7);
  transform: translateY(-2px);
  box-shadow: 0 12px 24px rgba(251, 191, 36, 0.25);
}

.nusantarum-pagination__button[disabled] {
  opacity: 0.5;
  cursor: not-allowed;
  transform: none;
  box-shadow: none;
}

.nusantarum-pagination__status {
  font-size: 0.9rem;
  color: rgba(255, 255, 255, 0.7);
}

.sync-status-list {
  display: grid;
  gap: 0.5rem;
}

.sync-status-item {
  display: flex;
  justify-content: space-between;
  gap: 0.5rem;
  font-size: 0.85rem;
  color: rgba(255, 255, 255, 0.7);
}

@media (max-width: 768px) {
  .nusantarum-table {
    min-width: 100%;
  }

  .nusantarum-table th,
  .nusantarum-table td {
    padding-inline: 1rem;
  }

  .nusantarum-tabs {
    gap: 0.5rem;
  }

  .nusantarum-tab {
    width: 100%;
    justify-content: center;
  }
}

src/app/web/static/css/profile.css.content:
.profile-page {
  display: flex;
  flex-direction: column;
  gap: 2.5rem;
}

.glass-panel {
  backdrop-filter: blur(24px) saturate(160%);
  background: linear-gradient(145deg, rgba(15, 23, 42, 0.75), rgba(30, 41, 59, 0.55));
  border: 1px solid rgba(148, 163, 184, 0.25);
  border-radius: var(--radius-lg);
  box-shadow: 0 25px 45px rgba(8, 15, 29, 0.45);
}

.profile-hero {
  display: grid;
  grid-template-columns: 160px 1fr;
  gap: 2rem;
  padding: 2.5rem;
  align-items: center;
}

.profile-hero__avatar {
  width: 160px;
  height: 160px;
  border-radius: 50%;
  overflow: hidden;
  border: 3px solid rgba(148, 163, 184, 0.35);
  box-shadow: 0 14px 28px rgba(8, 15, 29, 0.4);
}

.profile-hero__avatar img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.profile-hero__content {
  display: flex;
  flex-direction: column;
  gap: 1rem;
}

.profile-hero__headline {
  display: flex;
  justify-content: space-between;
  gap: 1rem;
  align-items: start;
}

.profile-hero__headline h1 {
  margin: 0;
  font-size: clamp(2.1rem, 2vw + 1.2rem, 2.6rem);
}

.profile-hero__username {
  margin: 0.25rem 0 0;
  color: var(--muted);
  font-weight: 500;
}

.profile-hero__bio {
  margin: 0;
  font-size: 1.05rem;
  color: var(--text-secondary);
}

.profile-hero__meta {
  list-style: none;
  display: flex;
  flex-wrap: wrap;
  gap: 0.75rem 1.5rem;
  padding: 0;
  margin: 0;
  color: var(--muted);
}

.profile-badges {
  list-style: none;
  margin: 0;
  padding: 0;
  display: flex;
  flex-wrap: wrap;
  gap: 0.75rem;
}

.profile-badge {
  display: inline-flex;
  align-items: center;
  gap: 0.65rem;
  padding: 0.45rem 1rem;
  border-radius: 999px;
  background: rgba(148, 163, 184, 0.18);
  border: 1px solid rgba(148, 163, 184, 0.25);
}

.profile-badge__label {
  font-weight: 600;
  letter-spacing: 0.02em;
}

.profile-badge__meta {
  font-size: 0.9rem;
  color: var(--accent-secondary);
}

.profile-badge--perfumer {
  background: linear-gradient(135deg, rgba(192, 38, 211, 0.25), rgba(14, 116, 144, 0.15));
  border-color: rgba(192, 38, 211, 0.35);
}

.profile-badge--brand-owner {
  background: linear-gradient(135deg, rgba(249, 115, 22, 0.25), rgba(244, 114, 182, 0.18));
  border-color: rgba(249, 115, 22, 0.35);
}

.profile-follow {
  display: flex;
  align-items: center;
  gap: 0.75rem;
}

.profile-follow__button,
.profile-follow__edit {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  border-radius: 999px;
  padding: 0.65rem 1.8rem;
  font-weight: 600;
  background: var(--accent-primary);
  color: #0f172a;
  border: none;
  cursor: pointer;
  text-decoration: none;
  transition: transform var(--transition-base), box-shadow var(--transition-base);
  box-shadow: 0 16px 30px rgba(192, 38, 211, 0.35);
}

.profile-follow__button:hover,
.profile-follow__edit:hover {
  transform: translateY(-2px);
}

.profile-follow__button--muted {
  background: rgba(148, 163, 184, 0.2);
  color: var(--text-primary);
  box-shadow: none;
  border: 1px solid rgba(148, 163, 184, 0.35);
}

.profile-follow__button--ghost {
  background: transparent;
  border: 1px solid rgba(148, 163, 184, 0.35);
  color: var(--text-secondary);
  box-shadow: none;
}

.profile-follow__button--ghost:hover {
  color: var(--text-primary);
}

.profile-stats {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(180px, 1fr));
  gap: 1rem;
  padding: 1.5rem 2rem;
}

.profile-stat {
  display: flex;
  flex-direction: column;
  gap: 0.35rem;
  text-align: center;
}

.profile-stat__label {
  color: var(--muted);
  font-size: 0.95rem;
}

.profile-stat__value {
  font-size: 1.6rem;
  font-weight: 600;
  color: var(--text-primary);
  background: none;
  border: none;
  cursor: pointer;
  padding: 0.25rem;
  transition: color var(--transition-base), transform var(--transition-base);
}

.profile-stat__value:hover {
  color: var(--accent-secondary);
  transform: translateY(-2px);
}

.profile-tabs {
  padding: 2rem;
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
}

.profile-edit-page {
  display: flex;
  justify-content: center;
  padding: 2rem 0 4rem;
}

.profile-edit-page__shell {
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
  width: min(720px, 100%);
}

.profile-edit-page__back {
  color: var(--accent-secondary);
  text-decoration: none;
  font-weight: 600;
  display: inline-flex;
  align-items: center;
  gap: 0.35rem;
}

.profile-edit-page__back:focus,
.profile-edit-page__back:hover {
  text-decoration: underline;
}

.profile-edit {
  padding: 2.25rem;
  display: flex;
  flex-direction: column;
  gap: 1.75rem;
}

.profile-edit__header {
  display: flex;
  gap: 1.25rem;
  align-items: center;
}

.profile-edit__avatar {
  width: 80px;
  height: 80px;
  border-radius: 50%;
  overflow: hidden;
  border: 2px solid rgba(148, 163, 184, 0.35);
  flex-shrink: 0;
}

.profile-edit__avatar img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.profile-edit__intro h1 {
  margin: 0;
  font-size: 1.6rem;
}

.profile-edit__intro p {
  margin: 0.35rem 0 0;
  color: var(--muted);
}

.profile-edit__feedback {
  padding: 0.75rem 1rem;
  border-radius: var(--radius-md);
  font-weight: 500;
}

.profile-edit__feedback--success {
  background: rgba(34, 197, 94, 0.12);
  color: rgb(134, 239, 172);
  border: 1px solid rgba(34, 197, 94, 0.35);
}

.profile-edit__feedback--info {
  background: rgba(148, 163, 184, 0.12);
  color: rgba(226, 232, 240, 0.9);
  border: 1px solid rgba(148, 163, 184, 0.3);
}

.profile-edit__form {
  display: flex;
  flex-direction: column;
  gap: 1.25rem;
}

.profile-edit__field {
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
}

.profile-edit__field label {
  font-weight: 600;
}

.profile-edit__field input,
.profile-edit__field textarea {
  background: rgba(15, 23, 42, 0.35);
  border: 1px solid rgba(148, 163, 184, 0.25);
  border-radius: var(--radius-md);
  padding: 0.75rem 1rem;
  color: var(--text-primary);
  font: inherit;
  transition: border-color var(--transition-base), box-shadow var(--transition-base);
}

.profile-edit__field input:focus,
.profile-edit__field textarea:focus {
  border-color: var(--accent-secondary);
  box-shadow: 0 0 0 3px rgba(192, 38, 211, 0.25);
  outline: none;
}

.profile-edit__grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(220px, 1fr));
  gap: 1rem;
}

.profile-edit__counter {
  color: var(--muted);
  display: block;
}

.profile-edit__actions {
  display: flex;
  flex-wrap: wrap;
  gap: 1rem;
  align-items: center;
}

.profile-edit__submit {
  background: var(--accent-primary);
  color: #0f172a;
  border: none;
  border-radius: 999px;
  padding: 0.75rem 1.75rem;
  font-weight: 600;
  cursor: pointer;
  transition: transform var(--transition-base), box-shadow var(--transition-base);
  box-shadow: 0 16px 30px rgba(192, 38, 211, 0.35);
}

.profile-edit__submit:hover {
  transform: translateY(-1px);
}

.profile-edit__submit:focus-visible {
  outline: 3px solid rgba(192, 38, 211, 0.6);
  outline-offset: 2px;
}

.profile-edit__cancel {
  color: var(--muted);
  text-decoration: none;
  font-weight: 500;
}

.profile-edit__cancel:focus,
.profile-edit__cancel:hover {
  text-decoration: underline;
}

@media (max-width: 640px) {
  .profile-edit {
    padding: 1.75rem;
  }

  .profile-edit__header {
    flex-direction: column;
    align-items: flex-start;
  }

  .profile-edit__avatar {
    width: 96px;
    height: 96px;
  }

  .profile-edit__actions {
    flex-direction: column;
    align-items: stretch;
  }

  .profile-edit__cancel {
    text-align: center;
  }
}

.profile-tabs__nav {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
  gap: 0.75rem;
}

.profile-tabs__button {
  padding: 0.75rem 1rem;
  border-radius: var(--radius-sm);
  background: rgba(148, 163, 184, 0.14);
  border: 1px solid transparent;
  color: var(--text-secondary);
  font-weight: 600;
  cursor: pointer;
  transition: background var(--transition-base), border var(--transition-base);
}

.profile-tabs__button--active,
.profile-tabs__button:hover {
  background: rgba(56, 189, 248, 0.18);
  border-color: rgba(56, 189, 248, 0.35);
  color: var(--text-primary);
}

.profile-tabs__content {
  display: flex;
  flex-direction: column;
  gap: 1.25rem;
}

.profile-timeline {
  list-style: none;
  display: flex;
  flex-direction: column;
  gap: 1rem;
  padding: 0;
  margin: 0;
}

.profile-timeline__item {
  padding: 1.5rem;
  border-radius: var(--radius-md);
  border: 1px solid rgba(148, 163, 184, 0.25);
}

.profile-timeline__title {
  margin: 0;
  font-size: 1.2rem;
}

.profile-timeline__time {
  color: var(--muted);
  font-size: 0.95rem;
}

.profile-list {
  list-style: none;
  padding: 1.25rem 1.5rem;
  margin: 0;
  border-radius: var(--radius-md);
  border: 1px solid rgba(148, 163, 184, 0.25);
  display: flex;
  flex-direction: column;
  gap: 0.85rem;
}

.profile-list__item {
  color: var(--text-secondary);
}

.profile-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(240px, 1fr));
  gap: 1.25rem;
}

.profile-card {
  padding: 1.5rem;
  border-radius: var(--radius-md);
  border: 1px solid rgba(148, 163, 184, 0.25);
  display: flex;
  flex-direction: column;
  gap: 0.75rem;
  transition: transform var(--transition-base), box-shadow var(--transition-base);
}

.profile-card:hover {
  transform: translateY(-4px);
  box-shadow: 0 20px 40px rgba(8, 15, 29, 0.45);
}

.profile-card__header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  gap: 0.75rem;
}

.profile-card__title {
  margin: 0;
}

.profile-card__brand {
  color: var(--muted);
  font-weight: 500;
}

.profile-card__notes {
  margin: 0;
  color: var(--text-secondary);
  font-weight: 500;
}

.profile-card__highlight {
  margin: 0;
  color: var(--muted);
  font-size: 0.95rem;
}

.profile-card__cta {
  align-self: flex-start;
  padding: 0.55rem 1.2rem;
  border-radius: 999px;
  border: 1px solid rgba(56, 189, 248, 0.45);
  color: var(--accent-secondary);
  text-decoration: none;
  font-weight: 600;
  transition: background var(--transition-base), color var(--transition-base);
}

.profile-card__cta:hover {
  background: rgba(56, 189, 248, 0.15);
  color: var(--text-primary);
}

.profile-card__badge {
  padding: 0.3rem 0.8rem;
  border-radius: 999px;
  font-size: 0.85rem;
  background: rgba(148, 163, 184, 0.18);
  border: 1px solid rgba(148, 163, 184, 0.25);
  text-transform: capitalize;
}

.profile-card__badge--active {
  background: rgba(56, 189, 248, 0.2);
  border-color: rgba(56, 189, 248, 0.45);
}

.profile-empty {
  padding: 2rem;
  text-align: center;
  border-radius: var(--radius-md);
  border: 1px dashed rgba(148, 163, 184, 0.35);
  color: var(--text-secondary);
}

.profile-empty--subtle {
  padding: 1rem 1.25rem;
  border-style: solid;
}

.profile-modal {
  position: fixed;
  inset: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  pointer-events: none;
}

.profile-modal__overlay {
  position: fixed;
  inset: 0;
  backdrop-filter: blur(6px);
  background: rgba(8, 15, 29, 0.45);
  display: flex;
  align-items: center;
  justify-content: center;
  pointer-events: auto;
  padding: 2rem;
}

.profile-modal__content {
  width: min(420px, 100%);
  padding: 1.5rem;
  border-radius: var(--radius-md);
  display: flex;
  flex-direction: column;
  gap: 1rem;
}

.profile-modal__header {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.profile-modal__header h2 {
  margin: 0;
}

.profile-modal__close {
  background: transparent;
  border: none;
  color: var(--text-secondary);
  font-size: 1.25rem;
  cursor: pointer;
}

.profile-modal__list {
  list-style: none;
  padding: 0;
  margin: 0;
  display: flex;
  flex-direction: column;
  gap: 0.85rem;
}

.profile-modal__item {
  display: flex;
  align-items: center;
  gap: 0.85rem;
}

.profile-modal__avatar {
  width: 40px;
  height: 40px;
  border-radius: 50%;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  background: rgba(148, 163, 184, 0.2);
  font-weight: 600;
}

.profile-modal__name {
  font-weight: 600;
}

.profile-modal__username {
  color: var(--muted);
  font-size: 0.9rem;
}

.profile-modal__empty {
  color: var(--text-secondary);
  text-align: center;
}

.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border: 0;
}

@media (max-width: 768px) {
  .profile-hero {
    grid-template-columns: 1fr;
    text-align: center;
  }

  .profile-hero__headline {
    flex-direction: column;
    align-items: center;
  }

  .profile-follow {
    justify-content: center;
  }

  .profile-stats {
    grid-template-columns: repeat(2, minmax(120px, 1fr));
    padding: 1.25rem;
  }

  .profile-tabs {
    padding: 1.5rem;
  }
}

src/app/web/static/css/purchase-workflow.content:
.purchase-hero {
  display: grid;
  grid-template-columns: minmax(0, 1fr) minmax(0, 320px);
  gap: 2rem;
  padding: 2.5rem;
  align-items: start;
}

.purchase-hero .hero-copy h1 {
  font-size: clamp(2.2rem, 3vw, 2.8rem);
  margin-bottom: 1rem;
}

.purchase-hero .hero-copy p {
  margin: 0;
}

.purchase-hero .badge {
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.35rem 1rem;
  border-radius: 999px;
  background: rgba(56, 189, 248, 0.12);
  color: var(--accent-secondary);
  font-weight: 600;
  letter-spacing: 0.02em;
  margin-bottom: 1.5rem;
}

.status-legend h2 {
  margin-top: 0;
  margin-bottom: 1rem;
  font-size: 1.35rem;
}

.status-legend ul {
  list-style: none;
  padding: 0;
  margin: 0;
  display: grid;
  gap: 1rem;
}

.status-legend li {
  display: grid;
  grid-template-columns: auto 1fr;
  gap: 0.75rem;
  align-items: start;
}

.status-icon {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  width: 2.25rem;
  height: 2.25rem;
  border-radius: 999px;
  background: rgba(148, 163, 184, 0.15);
  font-size: 1.25rem;
}

.status-label {
  margin: 0;
  color: var(--text-primary);
  font-weight: 600;
}

.status-description {
  margin: 0.35rem 0 0;
  font-size: 0.95rem;
}

.flow-nav {
  padding: 1.75rem;
  display: grid;
  gap: 1rem;
}

.flow-nav h2 {
  margin: 0;
  font-size: 1.4rem;
}

.flow-nav ol {
  list-style: none;
  padding: 0;
  margin: 0;
  display: flex;
  gap: 1rem;
  flex-wrap: wrap;
}

.flow-nav a {
  color: var(--text-primary);
  text-decoration: none;
  font-weight: 600;
  padding: 0.5rem 1.25rem;
  border-radius: 999px;
  border: 1px solid rgba(148, 163, 184, 0.35);
  background: rgba(15, 23, 42, 0.45);
  transition: transform var(--transition-base), border-color var(--transition-base);
}

.flow-nav a:hover {
  transform: translateY(-2px);
  border-color: rgba(56, 189, 248, 0.6);
}

.flow-section {
  padding: 2.25rem;
  display: grid;
  gap: 2rem;
}

.flow-header {
  display: grid;
  grid-template-columns: minmax(0, 1.2fr) minmax(0, 0.8fr);
  gap: 2.5rem;
  align-items: start;
}

.flow-summary {
  color: var(--text-secondary);
  margin-bottom: 0.75rem;
}

.flow-persona {
  margin: 0;
  color: var(--text-secondary);
}

.flow-meta {
  display: grid;
  gap: 1.5rem;
  justify-items: start;
}

.status-chip {
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.5rem 1.2rem;
  border-radius: 999px;
  font-weight: 600;
  border: 1px solid rgba(148, 163, 184, 0.4);
  background: rgba(15, 23, 42, 0.5);
}

.metric-list {
  background: rgba(15, 23, 42, 0.45);
  border: 1px solid rgba(148, 163, 184, 0.25);
  border-radius: var(--radius-md);
  padding: 1.25rem 1.5rem;
  box-shadow: inset 0 0 0 1px rgba(56, 189, 248, 0.08);
}

.metric-list h3 {
  margin-top: 0;
  margin-bottom: 0.75rem;
  font-size: 1.1rem;
}

.metric-list ul {
  margin: 0;
  padding-left: 1.1rem;
  display: grid;
  gap: 0.5rem;
}

.flow-steps {
  list-style: none;
  margin: 0;
  padding: 0;
  display: grid;
  gap: 1.5rem;
}

.flow-step {
  background: rgba(15, 23, 42, 0.55);
  border-radius: var(--radius-md);
  border: 1px solid rgba(148, 163, 184, 0.25);
  padding: 1.75rem;
  box-shadow: 0 16px 45px rgba(8, 15, 29, 0.35);
  backdrop-filter: blur(18px);
  transition: transform var(--transition-base), box-shadow var(--transition-base);
}

.flow-step:hover {
  transform: translateY(-4px);
  box-shadow: 0 24px 55px rgba(8, 15, 29, 0.45);
}

.step-header {
  display: grid;
  grid-template-columns: auto 1fr auto;
  gap: 1.25rem;
  align-items: center;
  margin-bottom: 1.25rem;
}

.step-index {
  width: 2.75rem;
  height: 2.75rem;
  border-radius: 999px;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  background: var(--accent-primary);
  color: #0f172a;
  font-weight: 700;
  font-size: 1.2rem;
  box-shadow: 0 12px 30px rgba(249, 115, 22, 0.25);
}

.step-stage {
  margin: 0;
  text-transform: uppercase;
  letter-spacing: 0.08em;
  font-size: 0.85rem;
  color: var(--accent-secondary);
}

.step-header h3 {
  margin: 0.25rem 0 0;
  font-size: 1.35rem;
}

.step-status {
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.35rem 0.9rem;
  border-radius: 999px;
  font-weight: 600;
  border: 1px solid rgba(148, 163, 184, 0.35);
  background: rgba(148, 163, 184, 0.12);
}

.step-body {
  display: grid;
  grid-template-columns: repeat(2, minmax(0, 1fr));
  gap: 1.5rem;
}

.step-body h4 {
  margin-top: 0;
  font-size: 1.05rem;
}

.step-body ul {
  margin: 0;
  padding-left: 1.1rem;
  display: grid;
  gap: 0.6rem;
}

.foundation-notes {
  padding: 2rem 2.25rem;
  display: grid;
  gap: 1rem;
}

.foundation-notes ul {
  margin: 0;
  padding-left: 1.2rem;
  display: grid;
  gap: 0.5rem;
}

/* Status color accents */
.step-status.status-live,
.status-chip.status-live,
.status-icon.status-live {
  border-color: rgba(52, 211, 153, 0.4);
  background: rgba(52, 211, 153, 0.18);
}

.step-status.status-ready,
.status-chip.status-ready,
.status-icon.status-ready {
  border-color: rgba(56, 189, 248, 0.45);
  background: rgba(56, 189, 248, 0.16);
}

.step-status.status-in-design,
.status-chip.status-in-design,
.status-icon.status-in-design {
  border-color: rgba(192, 132, 252, 0.4);
  background: rgba(192, 132, 252, 0.16);
}

.step-status.status-in-discovery,
.status-chip.status-in-discovery,
.status-icon.status-in-discovery {
  border-color: rgba(248, 180, 0, 0.4);
  background: rgba(248, 180, 0, 0.2);
}

@media (max-width: 1024px) {
  .purchase-hero {
    grid-template-columns: minmax(0, 1fr);
  }

  .status-legend {
    order: 2;
  }

  .flow-header {
    grid-template-columns: 1fr;
  }

  .flow-meta {
    justify-items: stretch;
  }

  .step-body {
    grid-template-columns: 1fr;
  }
}

@media (max-width: 640px) {
  .purchase-hero,
  .flow-section,
  .foundation-notes {
    padding: 1.75rem;
  }

  .flow-nav ol {
    flex-direction: column;
  }

  .step-header {
    grid-template-columns: 1fr;
    justify-items: start;
  }

  .step-status {
    margin-top: 0.75rem;
  }
}

src/app/web/static/css/ui-ux-tracker.css.content:
.top-anchor {
  position: relative;
  top: -120px;
}

.tracker-hero {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 2rem;
  padding: 2.5rem;
  align-items: start;
}

.tracker-hero .badge {
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.35rem 1rem;
  border-radius: 999px;
  background: rgba(148, 163, 184, 0.18);
  color: var(--text-secondary);
  font-size: 0.9rem;
  letter-spacing: 0.02em;
  text-transform: uppercase;
}

.tracker-hero h1 {
  margin-top: 1.2rem;
  margin-bottom: 1rem;
  font-size: clamp(1.8rem, 4vw, 2.6rem);
}

.tracker-hero p {
  margin: 0.75rem 0;
  max-width: 36ch;
}

.tracker-hero .hero-subcopy {
  color: var(--text-primary);
  font-weight: 500;
}

.status-legend {
  padding: 1.8rem;
  display: flex;
  flex-direction: column;
  gap: 1rem;
}

.status-legend h2 {
  margin: 0;
  font-size: 1.15rem;
}

.status-legend ul {
  list-style: none;
  margin: 0;
  padding: 0;
  display: flex;
  flex-direction: column;
  gap: 0.75rem;
}

.status-legend li {
  display: flex;
  align-items: center;
  gap: 0.65rem;
  font-weight: 500;
}

.status-icon {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  width: 1.8rem;
  height: 1.8rem;
  border-radius: 0.75rem;
  background: rgba(148, 163, 184, 0.12);
  font-size: 1rem;
}

.status-todo {
  color: var(--muted);
}

.status-progress {
  color: var(--accent-secondary);
}

.status-done {
  color: var(--success);
}

.section-nav {
  display: grid;
  gap: 1rem;
  padding: 1.8rem 2rem;
}

.section-nav h2 {
  margin: 0;
  font-size: 1.25rem;
}

.section-nav ol {
  margin: 0;
  padding-left: 1.2rem;
  display: grid;
  gap: 0.6rem;
}

.section-nav a {
  color: var(--text-secondary);
  text-decoration: none;
  position: relative;
}

.section-nav a::after {
  content: "";
  position: absolute;
  left: 0;
  bottom: -0.2rem;
  width: 100%;
  height: 2px;
  background: var(--accent-primary);
  transform: scaleX(0);
  transform-origin: left;
  transition: transform var(--transition-base);
}

.section-nav a:hover::after,
.section-nav a:focus-visible::after {
  transform: scaleX(1);
}

.tracker-section {
  padding: 2.25rem 2.4rem;
  display: flex;
  flex-direction: column;
  gap: 1.75rem;
}

.section-header {
  display: flex;
  align-items: start;
  justify-content: space-between;
  gap: 1.5rem;
}

.section-eyebrow {
  text-transform: uppercase;
  letter-spacing: 0.12em;
  font-size: 0.8rem;
  color: var(--muted);
  margin: 0;
}

.section-header h2 {
  margin: 0.25rem 0 0;
  font-size: clamp(1.5rem, 3vw, 2.1rem);
}

.section-top-link {
  text-decoration: none;
  color: var(--text-secondary);
  font-size: 1.4rem;
  transition: color var(--transition-base), transform var(--transition-base);
}

.section-top-link:hover,
.section-top-link:focus-visible {
  color: var(--accent-secondary);
  transform: translateY(-2px);
}

.task-grid {
  display: grid;
  gap: 1.4rem;
  grid-template-columns: repeat(auto-fit, minmax(240px, 1fr));
}

.task-card {
  display: flex;
  flex-direction: column;
  gap: 1rem;
  padding: 1.6rem;
  border-radius: var(--radius-md);
  background: rgba(15, 23, 42, 0.72);
  border: 1px solid rgba(148, 163, 184, 0.25);
  box-shadow: 0 20px 45px rgba(8, 15, 29, 0.35);
  transition: transform var(--transition-base), box-shadow var(--transition-base);
}

.task-card:hover {
  transform: translateY(-4px);
  box-shadow: 0 28px 60px rgba(8, 15, 29, 0.4);
}

.task-card header {
  display: flex;
  align-items: center;
  gap: 0.9rem;
}

.task-card h3,
.task-card h4 {
  margin: 0;
  font-size: 1.2rem;
}

.task-card p {
  margin: 0;
  color: var(--text-secondary);
}

.task-card footer {
  display: flex;
  flex-direction: column;
  gap: 0.3rem;
  margin-top: auto;
  font-size: 0.9rem;
}

.task-card .label {
  text-transform: uppercase;
  letter-spacing: 0.08em;
  color: var(--muted);
  font-weight: 600;
}

.task-card .deliverable {
  word-break: break-word;
  color: var(--text-primary);
}

.task-group {
  display: flex;
  flex-direction: column;
  gap: 1.25rem;
  padding: 1.6rem 1.8rem;
  border-radius: var(--radius-md);
  background: rgba(15, 23, 42, 0.55);
  border: 1px solid rgba(148, 163, 184, 0.2);
}

.task-group h3 {
  margin: 0;
  font-size: 1.3rem;
}

.grouped-section {
  display: grid;
  gap: 1.6rem;
}

.tracker-note {
  padding: 2rem 2.3rem;
  display: grid;
  gap: 0.75rem;
}

.tracker-note h2 {
  margin: 0;
}

@media (max-width: 720px) {
  .tracker-hero {
    padding: 2rem;
  }

  .section-header {
    flex-direction: column;
    align-items: flex-start;
  }

  .section-top-link {
    align-self: flex-end;
  }

  .task-group {
    padding: 1.4rem;
  }
}

