(function () {
  function normalise(value) {
    return (value || "").toString().toLowerCase().normalize("NFD").replace(/\p{Diacritic}/gu, "");
  }

  function activateTabGroup(
    buttons,
    targetKey,
    { buttonDatasetKey, panels, panelDatasetKey, shouldFocus = true } = {}
  ) {
    let activeButton = null;
    buttons.forEach((button) => {
      const key = buttonDatasetKey ? button.dataset[buttonDatasetKey] : undefined;
      const isActive = key === targetKey;
      button.classList.toggle("is-active", isActive);
      button.setAttribute("aria-selected", String(isActive));
      button.setAttribute("tabindex", isActive ? "0" : "-1");
      if (isActive) {
        activeButton = button;
      }
    });

    if (panels && panelDatasetKey) {
      panels.forEach((panel) => {
        const panelKey = panel.dataset[panelDatasetKey];
        const isMatch = panelKey === targetKey;
        panel.classList.toggle("is-hidden", !isMatch);
        if (isMatch) {
          panel.removeAttribute("hidden");
          panel.setAttribute("aria-hidden", "false");
        } else {
          panel.setAttribute("hidden", "hidden");
          panel.setAttribute("aria-hidden", "true");
        }
      });
    }

    if (activeButton && shouldFocus) {
      activeButton.focus();
    }

    return activeButton;
  }

  function bindProductFilters() {
    const buttons = Array.from(document.querySelectorAll("[data-product-filter]"));
    const rows = document.querySelectorAll("[data-product-row]");
    const searchInput = document.querySelector("[data-product-search]");
    if (!buttons.length) return;
    let activeFilter =
      buttons.find((button) => button.classList.contains("is-active"))?.dataset.productFilter ||
      "semua";

    activateTabGroup(buttons, activeFilter, {
      buttonDatasetKey: "productFilter",
      shouldFocus: false,
    });

    function apply() {
      const query = normalise(searchInput ? searchInput.value.trim() : "");
      rows.forEach((row) => {
        const status = row.dataset.status || "";
        const name = normalise(row.dataset.name || "");
        const sku = normalise(row.dataset.sku || "");
        const matchesFilter = activeFilter === "semua" || status === activeFilter;
        const matchesQuery = !query || name.includes(query) || sku.includes(query);
        const shouldShow = matchesFilter && matchesQuery;
        row.hidden = !shouldShow;
      });
    }

    buttons.forEach((button, index) => {
      button.addEventListener("click", () => {
        const target = button.dataset.productFilter;
        if (!target) return;
        activeFilter = target;
        activateTabGroup(buttons, target, { buttonDatasetKey: "productFilter" });
        apply();
      });

      button.addEventListener("keydown", (event) => {
        const { key } = event;
        const isNext = key === "ArrowRight" || key === "ArrowDown";
        const isPrev = key === "ArrowLeft" || key === "ArrowUp";
        if (!isNext && !isPrev) return;
        event.preventDefault();
        const offset = isNext ? 1 : -1;
        const nextIndex = (index + offset + buttons.length) % buttons.length;
        const nextButton = buttons[nextIndex];
        const target = nextButton?.dataset.productFilter;
        if (!target) return;
        activeFilter = target;
        activateTabGroup(buttons, target, { buttonDatasetKey: "productFilter" });
        apply();
      });
    });

    if (searchInput) {
      ["input", "change"].forEach((eventName) => {
        searchInput.addEventListener(eventName, apply);
      });
    }
  }

  function bindOrderFilters() {
    const buttons = Array.from(document.querySelectorAll("[data-order-filter]"));
    const rows = document.querySelectorAll("[data-order-row]");
    const searchInput = document.querySelector("[data-order-search]");
    if (!buttons.length) return;
    let activeFilter =
      buttons.find((button) => button.classList.contains("is-active"))?.dataset.orderFilter ||
      "semua";

    activateTabGroup(buttons, activeFilter, {
      buttonDatasetKey: "orderFilter",
      shouldFocus: false,
    });

    function apply() {
      const query = normalise(searchInput ? searchInput.value.trim() : "");
      rows.forEach((row) => {
        const status = normalise(row.dataset.status || "");
        const invoice = normalise(row.dataset.invoice || "");
        const customer = normalise(row.dataset.customer || "");
        const matchesFilter = activeFilter === "semua" || status === normalise(activeFilter);
        const matchesQuery = !query || invoice.includes(query) || customer.includes(query);
        const shouldShow = matchesFilter && matchesQuery;
        row.hidden = !shouldShow;
      });
    }

    buttons.forEach((button, index) => {
      button.addEventListener("click", () => {
        const target = button.dataset.orderFilter;
        if (!target) return;
        activeFilter = target;
        activateTabGroup(buttons, target, { buttonDatasetKey: "orderFilter" });
        apply();
      });

      button.addEventListener("keydown", (event) => {
        const { key } = event;
        const isNext = key === "ArrowRight" || key === "ArrowDown";
        const isPrev = key === "ArrowLeft" || key === "ArrowUp";
        if (!isNext && !isPrev) return;
        event.preventDefault();
        const offset = isNext ? 1 : -1;
        const nextIndex = (index + offset + buttons.length) % buttons.length;
        const nextButton = buttons[nextIndex];
        const target = nextButton?.dataset.orderFilter;
        if (!target) return;
        activeFilter = target;
        activateTabGroup(buttons, target, { buttonDatasetKey: "orderFilter" });
        apply();
      });
    });

    if (searchInput) {
      ["input", "change"].forEach((eventName) => {
        searchInput.addEventListener(eventName, apply);
      });
    }
  }

  function renderChart(chartEl) {
    const pointsAttr = chartEl.dataset.chartPoints;
    if (!pointsAttr) return;

    const points = pointsAttr
      .split(",")
      .map((value) => Number.parseFloat(value.trim()))
      .filter((value) => Number.isFinite(value));

    if (points.length < 2) return;

    const labelsAttr = chartEl.dataset.chartLabels || "";
    const labels = labelsAttr.split(",").map((label) => label.trim());

    const maxValue = Math.max(...points);
    const minValue = Math.min(...points);
    const range = maxValue - minValue || 1;

    const svgWidth = 100;
    const svgHeight = 40;
    const verticalPadding = 6;
    const usableHeight = svgHeight - verticalPadding * 2;
    const stepX = svgWidth / (points.length - 1);

    const line = chartEl.querySelector("[data-chart-line]");
    const area = chartEl.querySelector("[data-chart-area]");

    if (!line || !area) return;

    const coordinates = points.map((value, index) => {
      const normalised = (value - minValue) / range;
      const y = svgHeight - verticalPadding - normalised * usableHeight;
      const x = index * stepX;
      return [x, y];
    });

    const polylinePoints = coordinates.map(([x, y]) => `${x},${y}`).join(" ");
    line.setAttribute("points", polylinePoints);

    const areaPath = [
      `M0 ${svgHeight}`,
      `L${coordinates[0][0]} ${coordinates[0][1]}`,
      ...coordinates.slice(1).map(([x, y]) => `L${x} ${y}`),
      `L${coordinates[coordinates.length - 1][0]} ${svgHeight}`,
      "Z",
    ].join(" ");
    area.setAttribute("d", areaPath);

    if (labels.length === points.length) {
      const existingLabels = chartEl.querySelectorAll(".chart-label");
      existingLabels.forEach((label) => label.remove());
      labels.forEach((label, index) => {
        const marker = document.createElement("span");
        marker.className = "chart-label";
        marker.textContent = label;
        marker.style.left = `${(index / (labels.length - 1 || 1)) * 100}%`;
        chartEl.appendChild(marker);
      });
    }
  }

  function initialiseAnalytics() {
    const buttons = Array.from(document.querySelectorAll("[data-analytics-range]"));
    const panels = Array.from(document.querySelectorAll("[data-analytics-panel]"));
    if (!buttons.length) return;

    let activeRange =
      buttons.find((button) => button.classList.contains("is-active"))?.dataset.analyticsRange ||
      buttons[0]?.dataset.analyticsRange;

    activateTabGroup(buttons, activeRange, {
      buttonDatasetKey: "analyticsRange",
      panels,
      panelDatasetKey: "analyticsPanel",
      shouldFocus: false,
    });

    buttons.forEach((button, index) => {
      button.addEventListener("click", () => {
        const target = button.dataset.analyticsRange;
        if (!target) return;
        activeRange = target;
        activateTabGroup(buttons, target, {
          buttonDatasetKey: "analyticsRange",
          panels,
          panelDatasetKey: "analyticsPanel",
        });
      });

      button.addEventListener("keydown", (event) => {
        const { key } = event;
        const isNext = key === "ArrowRight" || key === "ArrowDown";
        const isPrev = key === "ArrowLeft" || key === "ArrowUp";
        if (!isNext && !isPrev) return;
        event.preventDefault();
        const offset = isNext ? 1 : -1;
        const nextIndex = (index + offset + buttons.length) % buttons.length;
        const nextButton = buttons[nextIndex];
        const target = nextButton?.dataset.analyticsRange;
        if (!target) return;
        activeRange = target;
        activateTabGroup(buttons, target, {
          buttonDatasetKey: "analyticsRange",
          panels,
          panelDatasetKey: "analyticsPanel",
        });
      });
    });

    document.querySelectorAll("[data-chart]").forEach((chartEl) => {
      renderChart(chartEl);
    });
  }

  function bindNotificationActions() {
    const list = document.querySelector("[data-notification-list]");
    if (!list) return;
    const emptyState = document.querySelector("[data-notification-empty]");

    list.addEventListener("click", (event) => {
      const target = event.target;
      if (!(target instanceof HTMLElement)) return;
      if (!target.matches("[data-dismiss-notification]")) return;
      const item = target.closest(".notification-item");
      if (!item) return;
      item.remove();
      if (!list.children.length && emptyState) {
        emptyState.hidden = false;
      }
    });
  }

  document.addEventListener("DOMContentLoaded", () => {
    bindProductFilters();
    bindOrderFilters();
    initialiseAnalytics();
    bindNotificationActions();
  });
})();

src/app/web/static/js/home-tabs.js.content:
(function () {
  const tabLists = document.querySelectorAll('[role="tablist"]');

  if (!tabLists.length) {
    return;
  }

  const activateTab = (tabs, panels, tab, { focus = true } = {}) => {
    const panelId = tab.getAttribute('aria-controls');
    const nextPanel = panelId ? document.getElementById(panelId) : null;

    tabs.forEach((candidate) => {
      const isSelected = candidate === tab;
      candidate.setAttribute('aria-selected', String(isSelected));
      candidate.tabIndex = isSelected ? 0 : -1;
      candidate.classList.toggle('is-active', isSelected);
    });

    panels.forEach((panel) => {
      if (!panel) {
        return;
      }

      const shouldShow = panel === nextPanel;

      if (shouldShow) {
        panel.removeAttribute('hidden');
      } else {
        panel.setAttribute('hidden', '');
      }
    });

    if (focus) {
      tab.focus();
    }
  };

  tabLists.forEach((tabList) => {
    const tabs = Array.from(tabList.querySelectorAll('[role="tab"]'));
    const panels = tabs.map((tab) => {
      const panelId = tab.getAttribute('aria-controls');
      return panelId ? document.getElementById(panelId) : null;
    });

    if (!tabs.length) {
      return;
    }

    const initiallySelected =
      tabs.find((tab) => tab.getAttribute('aria-selected') === 'true') || tabs[0];

    activateTab(tabs, panels, initiallySelected, { focus: false });

    tabList.addEventListener('click', (event) => {
      const target = event.target.closest('[role="tab"]');

      if (!target || !tabList.contains(target)) {
        return;
      }

      event.preventDefault();
      activateTab(tabs, panels, target);
    });

    tabList.addEventListener('keydown', (event) => {
      const { key } = event;
      const currentIndex = tabs.indexOf(document.activeElement);

      let nextIndex = null;

      if (key === 'ArrowRight' || key === 'ArrowDown') {
        nextIndex = (currentIndex + 1) % tabs.length;
      } else if (key === 'ArrowLeft' || key === 'ArrowUp') {
        nextIndex = (currentIndex - 1 + tabs.length) % tabs.length;
      } else if (key === 'Home') {
        nextIndex = 0;
      } else if (key === 'End') {
        nextIndex = tabs.length - 1;
      }

      if (nextIndex === null) {
        return;
      }

      event.preventDefault();
      const nextTab = tabs[nextIndex];
      activateTab(tabs, panels, nextTab);
    });
  });
})();

src/app/web/static/js/moderation-dashboa.content:
(function () {
  function copyToClipboard(text) {
    if (!text) return Promise.resolve(false);
    if (navigator.clipboard && typeof navigator.clipboard.writeText === "function") {
      return navigator.clipboard
        .writeText(text)
        .then(
          function () {
            return true;
          },
          function () {
            return legacyCopy(text);
          }
        );
    }
    return Promise.resolve(legacyCopy(text));
  }

  function legacyCopy(text) {
    var textarea = document.createElement("textarea");
    textarea.value = text;
    textarea.setAttribute("readonly", "readonly");
    textarea.style.position = "fixed";
    textarea.style.top = "-1000px";
    document.body.appendChild(textarea);
    textarea.select();
    var succeeded = false;
    try {
      succeeded = document.execCommand("copy");
    } catch (err) {
      succeeded = false;
    }
    document.body.removeChild(textarea);
    return succeeded;
  }

  function toggleState(element, attribute, state) {
    if (!element) return;
    element.setAttribute(attribute, String(Boolean(state)));
    if (state) {
      element.classList.add("is-active");
    } else {
      element.classList.remove("is-active");
    }
  }

  function activateTab(trigger, options) {
    if (!trigger) return;
    var opts = options || {};
    var container = trigger.closest("[data-tab-group]");
    if (!container) return;
    var tabs = container.querySelectorAll('[data-action="jump-section"]');
    Array.prototype.forEach.call(tabs, function (tab) {
      tab.classList.remove("is-active");
    });
    trigger.classList.add("is-active");
    if (!opts.silent) {
      ensureTabVisible(container, trigger);
    }
    requestFrame(function () {
      updateTabIndicator(container, trigger);
    });
  }

  function scrollToTarget(targetSelector) {
    if (!targetSelector) return false;
    var target = document.querySelector(targetSelector);
    if (!target) return false;
    if (typeof target.scrollIntoView === "function") {
      target.scrollIntoView({ behavior: "smooth", block: "start" });
      return true;
    }
    return false;
  }

  function handleAction(trigger, action, event) {
    switch (action) {
      case "share-report": {
        var shareUrl = trigger.dataset.shareUrl || window.location.href;
        copyToClipboard(shareUrl).then(function (success) {
          var message = success
            ? trigger.dataset.feedbackSuccess || "Tautan laporan disalin"
            : trigger.dataset.feedbackError || "Tidak dapat menyalin tautan";
          trigger.setAttribute("data-feedback", message);
          trigger.classList.add("has-feedback");
          window.setTimeout(function () {
            trigger.classList.remove("has-feedback");
            trigger.removeAttribute("data-feedback");
          }, 2000);
        });
        break;
      }
      case "refresh-snapshot": {
        window.location.reload();
        break;
      }
      case "focus-mode": {
        var focusTarget = trigger.dataset.focusTarget;
        if (focusTarget) {
          window.location.href = focusTarget;
          return;
        }
        document.body.classList.toggle("is-focus-mode");
        toggleState(trigger, "aria-pressed", document.body.classList.contains("is-focus-mode"));
        break;
      }
      case "bulk-action": {
        var targetSelector = trigger.dataset.bulkTarget;
        var bulkContainer = targetSelector ? document.querySelector(targetSelector) : null;
        var isActive = trigger.getAttribute("aria-pressed") === "true";
        isActive = !isActive;
        toggleState(trigger, "aria-pressed", isActive);
        if (bulkContainer) {
          bulkContainer.classList.toggle("is-bulk-select", isActive);
        }
        break;
      }
      case "invite-resend": {
        var email = trigger.dataset.inviteEmail || "pengguna";
        dispatchToast("Undangan dikirim ulang ke " + email + ".");
        break;
      }
      case "invite-cancel": {
        var cancelEmail = trigger.dataset.inviteEmail || "pengguna";
        dispatchToast("Undangan untuk " + cancelEmail + " dibatalkan.");
        break;
      }
      case "invite-open": {
        dispatchToast("Panel undangan dibuka.");
        break;
      }
      case "jump-section": {
        var targetSelector = trigger.dataset.target || trigger.getAttribute("href");
        var scrolled = scrollToTarget(targetSelector);
        activateTab(trigger);
        if (scrolled && event && typeof event.preventDefault === "function") {
          event.preventDefault();
        }
        return scrolled;
      }
      case "scroll-top": {
        if (event && typeof event.preventDefault === "function") {
          event.preventDefault();
        }
        if (typeof window.scrollTo === "function") {
          window.scrollTo({ top: 0, behavior: "smooth" });
        } else {
          window.scrollTo(0, 0);
        }
        return true;
      }
      case "view-sop":
      case "add-moderator":
      default: {
        break;
      }
    }
  }

  function dispatchToast(message) {
    if (!message) return;
    var event;
    try {
      event = new CustomEvent("mod:toast", { detail: { message: message } });
    } catch (error) {
      // IE fallback
      event = document.createEvent("CustomEvent");
      event.initCustomEvent("mod:toast", false, false, { message: message });
    }
    document.dispatchEvent(event);
  }

  function bindActions() {
    document.addEventListener("click", function (event) {
      var target = event.target;
      if (!(target instanceof Element)) {
        return;
      }
      var trigger = target.closest("[data-action]");
      if (!trigger) {
        return;
      }
      var action = trigger.dataset.action;
      if (!action) {
        return;
      }
      var handled = handleAction(trigger, action, event);
      if (handled === true && typeof event.preventDefault === "function") {
        event.preventDefault();
      }
    });
  }

  document.addEventListener("DOMContentLoaded", function () {
    bindActions();
    setupTabGroups();
    updateAllTabIndicators();
  });

  if (typeof window !== "undefined") {
    window.addEventListener("resize", function () {
      updateAllTabIndicators();
    });
    window.addEventListener("orientationchange", function () {
      updateAllTabIndicators();
    });
  }

  function ensureTabVisible(container, tab) {
    if (!container || !tab) return;
    var scroller = container.querySelector(".tab-scroller");
    if (!scroller) {
      return;
    }
    var left = tab.offsetLeft;
    var right = left + tab.offsetWidth;
    var viewStart = scroller.scrollLeft;
    var viewEnd = viewStart + scroller.clientWidth;
    var targetLeft = null;

    if (left < viewStart) {
      targetLeft = left;
    } else if (right > viewEnd) {
      targetLeft = right - scroller.clientWidth;
    }

    if (targetLeft === null) {
      return;
    }

    if (typeof scroller.scrollTo === "function") {
      scroller.scrollTo({ left: targetLeft, behavior: "smooth" });
    } else {
      scroller.scrollLeft = targetLeft;
    }
  }

  function requestFrame(callback) {
    if (typeof window !== "undefined" && typeof window.requestAnimationFrame === "function") {
      window.requestAnimationFrame(callback);
      return;
    }
    callback();
  }

  function updateTabIndicator(container, activeTab) {
    if (!container || !activeTab) return;
    var indicator = container.querySelector(".tab-indicator");
    if (!indicator) return;
    var scroller = container.querySelector(".tab-scroller") || container;
    var activeRect = activeTab.getBoundingClientRect();
    var scrollerRect = scroller.getBoundingClientRect();
    var scrollLeft = typeof scroller.scrollLeft === "number" ? scroller.scrollLeft : 0;
    var offset = activeRect.left - scrollerRect.left + scrollLeft;
    indicator.style.transform = "translateX(" + offset + "px)";
    indicator.style.width = activeRect.width + "px";
    indicator.classList.add("is-visible");
  }

  function updateAllTabIndicators() {
    var groups = document.querySelectorAll("[data-tab-group]");
    if (!groups.length) {
      return;
    }
    requestFrame(function () {
      Array.prototype.forEach.call(groups, function (group) {
        var active = group.querySelector(".tab-link.is-active");
        if (active) {
          updateTabIndicator(group, active);
        }
      });
    });
  }

  function setupTabGroups() {
    var groups = document.querySelectorAll("[data-tab-group]");
    if (!groups.length) {
      return;
    }

    Array.prototype.forEach.call(groups, function (group) {
      var tabs = group.querySelectorAll('[data-action="jump-section"]');
      if (!tabs.length) {
        return;
      }

      var initialActive = group.querySelector(".tab-link.is-active") || tabs[0];
      if (initialActive) {
        activateTab(initialActive, { silent: true });
      }

      if (typeof IntersectionObserver !== "function") {
        return;
      }

      var tabSections = [];
      Array.prototype.forEach.call(tabs, function (tab) {
        var targetSelector = tab.dataset.target || tab.getAttribute("href");
        if (!targetSelector) {
          return;
        }
        var section = document.querySelector(targetSelector);
        if (!section) {
          return;
        }
        tabSections.push({ tab: tab, section: section });
      });

      if (!tabSections.length) {
        return;
      }

      var observer = new IntersectionObserver(
        function (entries) {
          var candidate = null;
          for (var i = 0; i < entries.length; i += 1) {
            var entry = entries[i];
            if (!entry.isIntersecting) {
              continue;
            }
            if (!candidate || entry.boundingClientRect.top < candidate.boundingClientRect.top) {
              candidate = entry;
            }
          }

          if (!candidate) {
            return;
          }

          var matched = null;
          for (var j = 0; j < tabSections.length; j += 1) {
            if (tabSections[j].section === candidate.target) {
              matched = tabSections[j];
              break;
            }
          }

          if (matched) {
            activateTab(matched.tab, { silent: true });
          }
        },
        {
          rootMargin: "-55% 0px -35% 0px",
          threshold: [0.25, 0.5, 0.75],
        }
      );

      for (var index = 0; index < tabSections.length; index += 1) {
        observer.observe(tabSections[index].section);
      }
    });
  }
})();

